INSERT INTO `products` (`id`, `category_id`, `name`, `description`, `content`, `photo`, `hot`, `price`, `discount`) VALUES
(1, 4, 'BPI EAAs', '<p><strong>BPI EAAs</strong>&nbsp;bổ sung 9 nguồn amino axit thiết yếu ph&aacute;t triển cơ bắp. BPI EAAs nhập khẩu ch&iacute;nh h&atilde;ng, cam kết uy t&iacute;n, chất lượng, gi&aacute; rẻ nhất tại H&agrave; Nội</p>\r\n', '<p><span style=\"font-size:20px\"><strong>Giới thiệu v&agrave; lợi &iacute;ch BPI EAAs :</strong></span></p>\r\n\r\n<p>Sản phảm bổ sung 9 nguồn amino axit thiết yếu cho cơ bắp ph&aacute;t triển to&agrave;n diện. Đ&acirc;y l&agrave; nguồn amino axit m&agrave; cơ thể kh&ocirc;ng tự tổng hợp được, cần bổ sung từ b&ecirc;n ngo&agrave;i, c&oacute; nhiều vai tr&ograve; li&ecirc;n quan tới ph&aacute;t triển cơ bắp, tổng hợp protein v&agrave; phục hồi cơ bắp khỏe mạnh.</p>\r\n\r\n<p>BPI EAAs l&agrave; sản phẩm được nghi&ecirc;n cứu v&agrave; sản xuất bởi BPI Sport : thương hiệu lớn h&agrave;ng đầu tại Mỹ về thực phẩm bổ sung với những sản phẩm đang v&ocirc; c&ugrave;ng HOT hiện nay khẳng định chất lượng như :&nbsp;Whey tăng cơ ISO HD , sữa tăng c&acirc;n&nbsp;Bulk Muscle XL , Best bcaa&nbsp;phục hồi cơ giảm mỡ , &hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>C&ocirc;ng dụng của BPI EAAs :</strong></h3>\r\n\r\n<p>BPI EAAs cung cấp đầy đủ 9 loại amino acid thiết yếu cho cơ thể phục hồi v&agrave; ph&aacute;t triển cơ bắp tối ưu hơn. Với h&agrave;m lượng 4g EAAs mỗi lần d&ugrave;ng mang tới hiệu quả :&nbsp;</p>\r\n\r\n<ul>\r\n	<li>L &ndash; Leucine : amino acid v&ocirc; c&ugrave;ng quan trong cho qu&aacute; tr&igrave;nh tổng hợp Protein.</li>\r\n	<li>L &ndash; Valine : hỗ trợ phục hồi c&aacute;c m&ocirc; cơ bị tổn thương, ph&aacute;t triển cơ bắp.</li>\r\n	<li>L &ndash; Isoleucine : hỗ trợ phục hồi cơ&nbsp;bắp v&agrave; tổng hợp Hemoglobin , điều chỉnh lượng đường trong m&aacute;u v&agrave; mức năng lượng của cơ thể.</li>\r\n	<li>L &ndash; Lysine : đ&oacute;ng vai tr&ograve; trong qu&aacute; tr&igrave;nh ph&aacute;t triển, phục hồi c&aacute;c m&ocirc; v&agrave; hấp thụ chất dinh dưỡng.</li>\r\n	<li>L &ndash; Phenylalanine : đ&oacute;ng vai tr&ograve; trong qu&aacute; tr&igrave;nh tổng hợp c&aacute;c amino acid. Cũng l&agrave; một amino acid phụ tr&aacute;ch ch&iacute;nh cho c&aacute;c hoạt động của hormone quan trọng trong cơ thể như Dopamine(đ&oacute;ng một số vai tr&ograve; quan trọng trong bộ n&atilde;o v&agrave; cơ thể con người), Adrenaline (Đ&acirc;y l&agrave; một h&oacute;c-m&ocirc;n c&oacute; t&aacute;c dụng tr&ecirc;n thần kinh giao cảm, được sản xuất bởi cơ thể khi bạn sợ h&atilde;i, tức giận hay th&iacute;ch th&uacute;, c&aacute;i l&agrave;m cho nhịp tim của bạn đập nhanh hơn v&agrave; cơ thể chuẩn bị cho những phản ứng chống lại nguy hiểm) v&agrave; Noradrenaline.</li>\r\n	<li>L &ndash; Threonine : hỗ trợ tổng hợp protein, x&acirc;y dựng cơ bắp.</li>\r\n	<li>L &ndash; Histidine : hỗ trợ qu&aacute; tr&igrave;nh tổng hợp Protein.</li>\r\n	<li>L &ndash; Methionine : hỗ trợ phục hồi c&aacute;c m&ocirc; cơ trong cơ thể.</li>\r\n	<li>L &ndash; Trytophan : l&agrave; amino acid quan trọng nhất trong qu&aacute; tr&igrave;nh tổng hợp protein v&agrave; cũng l&agrave; tiền đề hoạt động cho Seratonin (được tổng hợp trong tế b&agrave;o thần kinh Serotonergic trong thần kinh trung ương, nơi n&oacute; c&oacute; nhiều chức năng kh&aacute;c nhau: điều chỉnh t&acirc;m trạng, sự th&egrave;m ăn, giấc ngủ, co cơ v&agrave; một số chức năng thuộc về nhận thức) v&agrave; Melatonin ( đ&oacute;ng vai tr&ograve; quan trọng trong việc duy tr&igrave; nhịp sống b&igrave;nh thường của một số chức năng sinh l&yacute;, kiểm so&aacute;t chất lượng giấc ngủ ).</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>C&aacute;ch sử dụng BPI EAAs tốt nhất :</strong></h3>\r\n\r\n<p><strong>BPI EAAs</strong>&nbsp;c&oacute; thể sử dụng trong rất nhiều thời điểm, n&ecirc;n bổ sung sử dụng tốt nhất l&agrave; thời điểm :</p>\r\n\r\n<ul>\r\n	<li>Giữ bữa ăn ch&iacute;nh</li>\r\n	<li>Trước buổi tập luyện</li>\r\n	<li>Sau buổi tập luyện</li>\r\n</ul>\r\n\r\n<p><strong>+&nbsp;</strong>Đ&oacute; l&agrave; những thời điểm cơ thể mệt mỏi, suy nhược cần bổ sung EAAs hỗ trợ phục hồi cơ bắp v&agrave; ph&aacute;t huy tối đa t&aacute;c dụng, tăng tổng hợp protein v&agrave; x&acirc;y dựng cơ bắp hiệu quả.</p>\r\n\r\n<p><strong>+&nbsp;</strong>D&ugrave;ng 1 muỗng gạt bằng&nbsp;BPI EAAs&nbsp;với 300-500ml nước lọc ( c&oacute; thể cho th&ecirc;m nước nếu sử dụng trong tập ), lắc đều đến khi tan hết v&agrave; sử dụng ngay, c&oacute; thể bỏ th&ecirc;m đ&aacute; lạnh để ngon hơn.</p>\r\n\r\n<p><strong>+&nbsp;</strong>EAAs c&oacute; thể pha chung sử dụng với whey protein&nbsp;ngay sau buổi tập hoặc c&aacute;c sản phẩm bổ sung kh&aacute;c trước tập, trong buổi tập để đạt hiệu quả tốt hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '15980273641.jpg', 0, 550000, 24),
(4, 5, 'Lipo 6 Black Hers', '<p><strong>Lipo 6 Black Hers</strong>&nbsp;Ultra Concentrate&nbsp;l&agrave; d&ograve;ng sản phẩm giảm mỡ giảm c&acirc;n d&agrave;nh cho phụ nữ tập gym.tập thể h&igrave;nh. Lipo 6 Black Hers ch&iacute;nh h&atilde;ng, cam kết chất lượng, gi&aacute; rẻ nhất tại H&agrave; Nội</p>\r\n', '<h2><strong>Giới thiệu Lipo 6 Black Hers :</strong></h2>\r\n\r\n<p>Một c&ocirc;ng thức đốt mỡ &ndash; giảm c&acirc;n chuy&ecirc;n biệt d&agrave;nh ri&ecirc;ng cho nữ :&nbsp;<strong>Lipo 6 Black Hers</strong>&nbsp;l&agrave; sản phẩm tăng cường trao đổi chất, sinh nhiệt đốt mỡ được chiết xuất ho&agrave;n to&agrave;n tự 100% tự nhi&ecirc;n, mang tới hiệu quả tuyệt vời trong việc cải thiện v&oacute;c d&aacute;ng, d&agrave;nh cho chị em phụ nữ tập gym, tập thể h&igrave;nh sử dụng.</p>\r\n\r\n<p>Lipo 6 Black Hers hỗ trợ cải thiện hiệu suất tập luyện bền bỉ hơn, giảm th&egrave;m ăn hỗ trợ ăn ki&ecirc;ng hiệu quả.</p>\r\n\r\n<p>Hiệu quả sẽ được tăng l&ecirc;n đ&aacute;ng kể nếu như đối tượng sử dụng kết hợp với chế độ ăn ki&ecirc;ng v&agrave; tập luyện ph&ugrave; hợp, Lipo 6 Black Hers gi&uacute;p kiềm chế cơn th&egrave;m ăn của bạn. Kh&ocirc;ng giống những sản phẩm giảm mỡ th&ocirc;ng thường l&agrave;m giảm việc đ&oacute;i bụng khiến bạn cảm thấy mệt mỏi hơn.&nbsp;</p>\r\n\r\n<p><strong>Caffeine :</strong></p>\r\n\r\n<ul>\r\n	<li>Caffeine c&oacute; trong rất nhiều trong c&aacute;c loại thực vật m&agrave; điển h&igrave;nh l&agrave; l&aacute; tr&agrave;, c&agrave; ph&ecirc; v&agrave; ca cao.</li>\r\n	<li>Caffeine l&agrave;m k&iacute;ch th&iacute;ch hệ thần kinh hệ thần kinh trung ương v&agrave; cơ thể bạn khiến n&oacute; hoạt động tập trung v&agrave; giảm thiểu mệt mỏi.</li>\r\n	<li>K&iacute;ch th&iacute;ch cơ thể sản sinh năng lượng ở mức cao hơn qua đ&oacute; tăng hiệu quả giảm c&acirc;n v&agrave; giảm mỡ.</li>\r\n</ul>\r\n\r\n<p><strong>Yohimbine HCL, Advantra Z* v&agrave; Zingerone :</strong></p>\r\n\r\n<ul>\r\n	<li>Nhiều nghi&ecirc;n cứu đ&atilde; chỉ ra rằng bộ ba hợp chất n&agrave;y gi&uacute;p cơ thể tăng khả năng trao đổi chất của cơ thể. Tăng cường đốt ch&aacute;y năng lượng th&uacute;c đẩy qu&aacute; tr&igrave;nh đốt mỡ.</li>\r\n	<li>Yohimbe tăng cường khả năng tỉnh t&aacute;o, tập trung v&agrave; duy tr&igrave; tinh thần phấn chấn, giảm mệt mỏi đặc biệt trong chế độ ăn ki&ecirc;ng.</li>\r\n</ul>\r\n\r\n<p><strong>Vitamin B12 :</strong></p>\r\n\r\n<ul>\r\n	<li>Vitamin B12 rất quan trọng để duy tr&igrave; sự khỏe mạnh của qu&aacute; tr&igrave;nh trao đổi chất, c&aacute;c tế b&agrave;o m&aacute;u v&agrave; tế b&agrave;o thần kinh.</li>\r\n</ul>\r\n\r\n<p><strong>Vitamin B9 (acid folic):</strong></p>\r\n\r\n<ul>\r\n	<li>Gi&uacute;p cơ thể duy tr&igrave; sản sinh c&aacute;c tế b&agrave;o mới, tăng khả năng t&aacute;i tạo lại c&aacute;c tế b&agrave;o mới của cơ thể.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng giảm mỡ tuyệt vời của Lipo 6 Black Hers :</strong></h3>\r\n\r\n<ul>\r\n	<li>Tăng cường khả năng tập trung tỉnh t&aacute;o, loại bỏ cảm gi&aacute;c lờ đờ trước khi đến ph&ograve;ng gym, tạo cảm hứng tập luyện.</li>\r\n	<li>Đẩy cao qu&aacute; tr&igrave;nh sản sinh năng lượng th&ocirc;ng qua sự sinh nhiệt của cơ thể, nghĩa l&agrave; sẽ tăng lượng calo ti&ecirc;u thụ l&ecirc;n một mức cao hơn.</li>\r\n	<li>Hỗ trợ qu&aacute; tr&igrave;nh giảm c&acirc;n nhanh hiệu quả, qua sự th&uacute;c đẩy trao đổi chất v&agrave; k&iacute;ch th&iacute;ch đốt ch&aacute;y mỡ ở cường độ cao.</li>\r\n	<li>Sản phẩm th&iacute;ch hợp v&agrave; đặc biệt hiệu quả với nữ giới.</li>\r\n	<li>Hỗ trợ sự phục hồi, tăng cường sức khỏe ở mức độ tế b&agrave;o</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>C&aacute;ch sử dụng Lipo 6 Black Hers Ultra Concentrate :</strong></h3>\r\n\r\n<p><strong>Lipo 6 Black Hers</strong>&nbsp;đốt mỡ giảm c&acirc;n l&agrave; sản phẩm chứa c&aacute;c chất tăng cường khả năng trao đổi chất, ức chế sự th&egrave;m ăn v&agrave; cảm gi&aacute;c đ&oacute;i, gi&uacute;p sử dụng c&aacute;c m&ocirc; mỡ v&agrave; chất b&eacute;o l&agrave;m năng lượng.</p>\r\n\r\n<p>Thời điểm sử dụng tốt nhất :</p>\r\n\r\n<p>+ S&aacute;ng sớm khi ngủ dậy sử dụng 1 vi&ecirc;n, trước bữa ăn s&aacute;ng 30p</p>\r\n\r\n<p>+ Trước buổi tập 15-20p sử dụng 1 vi&ecirc;n</p>\r\n\r\n<p>Lưu &yacute; :</p>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng n&ecirc;n sử dụng s&aacute;t giờ ngủ v&igrave; sản phẩm c&oacute; caffeine g&acirc;y mất ngủ</li>\r\n	<li>Kh&ocirc;ng n&ecirc;n sử dụng qu&aacute; 2 vi&ecirc;n / ng&agrave;y</li>\r\n	<li>Trong 7 ng&agrave;y đầu c&oacute; thể sử dụng 1 vi&ecirc;n/ ng&agrave;y v&agrave; s&aacute;ng trước bữa ăn rồi tăng dần</li>\r\n</ul>\r\n', '15980276942.jpg', 1, 650000, 0),
(5, 1, 'Rule 1 Protein', '<p>Whey<strong>&nbsp;Rule 1&nbsp;</strong>Protein l&agrave; sản phẩm ph&aacute;t triển cơ bắp cung cấp 100% Whey Isolate v&agrave; Hydrolyzed hấp thu nhanh.&nbsp;</p>\r\n', '<h2><strong>Rule 1 Protein</strong>&nbsp;<strong>&ndash; C&ocirc;ng thức tăng cơ ưu việt nhất c&ugrave;ng Whey Protein Isolate v&agrave; Whey Protein Hydrolysate</strong></h2>\r\n\r\n<p>Một qu&aacute; tr&igrave;nh tập luyện chăm chỉ chắc chắn phải đi k&egrave;m với nguồn dinh dưỡng quan trọng, c&oacute; khả năng hấp thu nhanh vượt trội sau buổi tập như&nbsp;<strong>Rule 1 Protein</strong>&nbsp;để gi&uacute;p bạn phục hồi v&agrave; x&acirc;y dựng cơ bắp hiệu quả.&nbsp;</p>\r\n\r\n<p>Với c&ocirc;ng thức vượt trội&nbsp;Whey Protein Isolate v&agrave; Whey Protein Hydrolysate&nbsp;tinh khiết, nhiều BCAA hơn ( l&ecirc;n tới 6 gram &ndash; mỗi lần d&ugrave;ng ) , gi&agrave;u Glutamine, kh&ocirc;ng chứa tạp chất ,&hellip; từ Rule 1 Protein chắc chắn sẽ đảm bảo nguồn protein quan trọng để gi&uacute;p bạn ph&aacute;t triển cơ bắp hiệu tối ưu nhất.</p>\r\n\r\n<h2><strong>Tại sao Rule 1 Protein lại được người tập gym, tập thể h&igrave;nh đ&aacute;nh gi&aacute; cao</strong></h2>\r\n\r\n<p><strong>Rule One Protein</strong>&nbsp;với lợi thế l&agrave; một thương hiệu mới th&agrave;nh lập từ năm 2014 đ&atilde; kh&ocirc;ng tiếc chi ph&iacute; ứng dụng những c&ocirc;ng nghệ sản xuất ti&ecirc;n tiến, hiện đại nhất tạo n&ecirc;n sản phẩm tăng cơ bắp h&agrave;ng đầu thị trường hiện nay.&nbsp;</p>\r\n\r\n<p>Với c&ocirc;ng nghệ CFM tạo n&ecirc;n nguồn&nbsp;Whey Protein&nbsp;Hydrolysate hấp thu nhanh nhất, tinh khiết nhất,&nbsp;kh&ocirc;ng c&ograve;n chứa chất b&eacute;o, tinh bột, cholesterol hoặc c&aacute;c tạp chất kh&aacute;c. Rule One Protein cũng l&agrave; sản phẩm dẫn đầu trong ti&ecirc;u chuẩn ZERO gums , ZERO creamers , tạo n&ecirc;n hương vị ho&agrave;n to&agrave;n tự nhi&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Th&agrave;nh phần ch&iacute;nh của Rule 1 Protein</strong></h2>\r\n\r\n<p><strong>Rule 1 Protein</strong>&nbsp;lu&ocirc;n được người tập gym, tập thể h&igrave;nh đ&aacute;nh gi&aacute; cao bởi bảng th&agrave;nh phần chất lượng mới tới hiệu quả ph&aacute;t triển cơ bắp vượt trội.&nbsp;</p>\r\n\r\n<h3><strong>Ultra Pure Whey Isolate</strong></h3>\r\n\r\n<ul>\r\n	<li>Cung cấp 25g protein mỗi lần d&ugrave;ng, với th&agrave;nh phần ch&iacute;nh l&agrave; Whey Isolate hấp thu nhanh.</li>\r\n	<li>Chứa tới 6g BCAA phục hồi cơ bắp hiệu quả.</li>\r\n	<li>H&agrave;m lượng 87% protein mỗi serving , cực kỳ tinh khiết.</li>\r\n	<li>Kh&ocirc;ng chứa whey concentrate, lactose hay chất b&eacute;o.</li>\r\n	<li>Cải thiện sức bền, sức mạnh v&agrave; ph&aacute;t triển cơ bắp.</li>\r\n</ul>\r\n\r\n<h3><strong>Whey Protein Hydrolysate</strong></h3>\r\n\r\n<ul>\r\n	<li>Bổ sung th&ecirc;m Whey protein hydrolysate n&acirc;ng tầm chất lượng.</li>\r\n	<li>Hấp thu nhanh nhất, c&oacute; gi&aacute; trị sinh học cao nhất.</li>\r\n	<li>Whey Protein Hydrolysate c&oacute; lợi &iacute;ch ph&aacute;t triển cơ bắp vượt trội.</li>\r\n	<li>Cung cấp nguồn axit amin lập tức, phục hồi cơ bắp</li>\r\n	<li>Gi&uacute;p tổng hợp protein cơ bắp v&agrave; ngăn ngừa dị h&oacute;a cơ bắp hiệu quả.</li>\r\n</ul>\r\n\r\n<h3><strong>Bổ sung th&ecirc;m BCAA ( Branched-chain Amino Acid )</strong></h3>\r\n\r\n<ul>\r\n	<li>Cung cấp nhiều hơn với 6g&nbsp;BCAA&nbsp;mỗi lần d&ugrave;ng.</li>\r\n	<li>Hỗ trợ tổng hợp protein, chống dị h&oacute;a v&agrave; phục hồi cơ bắp sau tập.</li>\r\n	<li>Kiểm so&aacute;t đường huyết v&agrave; hỗ trợ giảm b&eacute;o hiệu quả.</li>\r\n	<li>Bổ sung nhiều BCAA c&ograve;n được chứng minh c&oacute; khả năng hỗ trợ tăng testosterone v&agrave; giảm cortisol, x&acirc;y dựng v&agrave; chống dị h&oacute;a cơ bắp hiệu quả hơn.</li>\r\n</ul>\r\n\r\n<h3><strong>C&ocirc;ng thức si&ecirc;u tinh khiết của Rule 1 Protein</strong></h3>\r\n\r\n<ul>\r\n	<li>HO&Agrave;N TO&Agrave;N KH&Ocirc;NG ĐƯỜNG.</li>\r\n	<li>HO&Agrave;N TO&Agrave;N KH&Ocirc;NG LACTOSE.</li>\r\n	<li>HO&Agrave;N TO&Agrave;N KH&Ocirc;NG GLUTEN.</li>\r\n</ul>\r\n\r\n<p>Mỗi lần d&ugrave;ng&nbsp;<strong>Rule 1 Protein</strong>&nbsp;chỉ cung cấp ho&agrave;n to&agrave;n nguồn protein tinh khiết, kh&ocirc;ng chứa tạp chất, kh&ocirc;ng amino spiking, kh&ocirc;ng chất cấm.</p>\r\n\r\n<p>Thời điểm sử dụng&nbsp;<strong>Whey Rule 1 Protein&nbsp;</strong>tốt nhất l&agrave; s&aacute;ng sớm ngay khi ngủ dậy 1 lần v&agrave; ngay sau khi tập luyện sử dụng 1 lần.</p>\r\n\r\n<p>Mỗi lần d&ugrave;ng 1 muỗng pha với 250-300ml nước lọc ( th&ecirc;m đ&aacute; lạnh sẽ ngon hơn ) . Lưu &yacute;&nbsp;kh&ocirc;ng n&ecirc;n&nbsp;pha Rule 1 Protein với nước n&oacute;ng, sử dụng liền sau khi pha,&nbsp;kh&ocirc;ng n&ecirc;n&nbsp;để l&acirc;u kh&ocirc;ng đảm bảo dinh dưỡng.</p>\r\n', '15980280033.jpg', 1, 1800000, 23),
(6, 1, 'Whey Gold Standard 5lbs', '<p><strong>Whey Gold Standard</strong>&nbsp;100% l&agrave; sản phẩm Whey tăng cơ với hơn 30 năm uy t&iacute;n thương hiệu h&agrave;ng đầu thế giới,cam kết ch&iacute;nh h&atilde;ng, chất lượng v&agrave; gi&aacute; rẻ nhất H&agrave; Nội.</p>\r\n', '<h2><strong>Whey Gold Standard &ndash; Tăng cơ ti&ecirc;u chuẩn V&agrave;ng</strong></h2>\r\n\r\n<p><strong>ON Whey Gold</strong>&nbsp;đ&atilde; khẳng định chất lượng, hiệu quả tăng cơ cũng như t&ecirc;n tuổi tr&ecirc;n thị trường Việt Nam với tr&ecirc;n 10 năm l&agrave; sản phẩm được tin d&ugrave;ng, b&aacute;n chạy nhất cho tới hiện nay.&nbsp;<strong>Whey Gold Standard</strong>&nbsp;&ndash; tăng cơ ti&ecirc;u chuẩn V&agrave;ng lu&ocirc;n l&agrave; lựa chọn ưu ti&ecirc;n h&agrave;ng đầu bởi 4 l&yacute; do sau đ&acirc;y:</p>\r\n\r\n<ol>\r\n	<li>L&agrave; sản phẩm Whey Protein b&aacute;n chạy nhất trong 10 năm liền. Sản phẩm được đ&aacute;nh gi&aacute; cao tr&ecirc;n thị trường bởi rất nhiều vận động vi&ecirc;n t&ecirc;n tuổi c&ugrave;ng người tập gym, tập thể h&igrave;nh tr&ecirc;n khắp thế giới tin d&ugrave;ng.</li>\r\n	<li>H&atilde;ng&nbsp;Optimum Nutrition&nbsp;( ON ) l&agrave; h&atilde;ng sản xuất thực phẩm bổ sung h&agrave;ng đầu của Mỹ. H&atilde;ng th&agrave;nh lập năm 1986, l&agrave; một trong những c&ocirc;ng ty c&oacute; cơ sở sản xuất tu&acirc;n thủ theo GMP&nbsp;v&agrave; được NSF&nbsp;chứng nhận đảm bảo về chất lượng sản phẩm.</li>\r\n	<li><strong>Whey Gold Standard</strong>&nbsp;cung cấp nguồn protein tinh khiết kết hợp giữa&nbsp;Whey Protein Isolate&nbsp;v&agrave; Whey Protein Concentrate. Th&agrave;nh phần loại bỏ tạp chất, mang gi&aacute; trị sinh học cao.</li>\r\n	<li>Hương vị lu&ocirc;n được chiết xuất từ tự nhi&ecirc;n, đa dạng về hương vị&nbsp; ph&ugrave; hợp cho đại đa số người sử dụng.</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>24g Protein tinh khiết hấp thu nhanh gi&uacute;p ph&aacute;t triển khối cơ nạc của bạn hiệu quả.</li>\r\n	<li>4g Glutamine gi&uacute;p l&agrave;m giảm nhức mỏi cơ bắp, tăng khả năng hồi phục sau mỗi buổi tập.</li>\r\n	<li>5.5g BCAA&nbsp;hỗ trợ phục hồi cơ, chống dị h&oacute;a cơ bắp, tăng khả năng tổng hợp protein sau tập.</li>\r\n	<li>H&agrave;m lượng tạp chất được loại bỏ gần như ho&agrave;n to&agrave;n.</li>\r\n	<li>Whey Gold bổ sung th&ecirc;m enzyme hỗ trợ ti&ecirc;u h&oacute;a, tối ưu khả năng hấp thu nguồn protein hiệu quả.</li>\r\n</ul>\r\n\r\n<h2><strong>Ưu điểm vượt trội của Whey Gold Standard</strong></h2>\r\n\r\n<p>Phục hồi cơ bắp sau những giờ tập luyện mệt mỏi. X&acirc;y dựng cho bạn khối cơ nạc m&agrave; bạn mong muốn. Cơ bắp bạn bị tổn thương sau tập, protein lu&ocirc;n cần thiết trong qu&aacute; tr&igrave;nh t&aacute;i tạo v&agrave; x&acirc;y dựng khối cơ bắp. Tăng cường khả năng tổng hợp v&agrave; hấp thu protein một c&aacute;ch tối đa nhất.</p>\r\n\r\n<p>Tăng khả năng hấp thu dinh dưỡng, tăng sức đề kh&aacute;ng, ổn định hệ ti&ecirc;u h&oacute;a của bạn.</p>\r\n\r\n<p>Ph&ugrave; hợp với những bạn đang c&oacute; chế độ ăn thấp calories trong giai đoạn giảm c&acirc;n. Sản phẩm bổ sung kh&ocirc;ng thế thiếu hỗ trợ bạn thiết lập chế độ ăn cần thiết cho cơ thể. Lu&ocirc;n đảm bảo đủ dinh dưỡng cho mọi hoạt động.</p>\r\n\r\n<h2><strong>C&aacute;ch sử dụng Whey Gold đ&uacute;ng c&aacute;ch</strong></h2>\r\n\r\n<p>Thời điểm sử dụng&nbsp;<strong>Whey Gold Standard&nbsp;</strong>tốt nhất được wheyshop khuy&ecirc;n d&ugrave;ng l&agrave; s&aacute;ng sớm ngay khi ngủ dậy 1 lần v&agrave; ngay sau khi tập luyện sử dụng 1 lần.</p>\r\n\r\n<p>Mỗi lần d&ugrave;ng 1 muỗng pha với 250-300ml nước lọc ( th&ecirc;m đ&aacute; lạnh sẽ ngon hơn ). Lưu &yacute;&nbsp;kh&ocirc;ng n&ecirc;n&nbsp;pha Whey Gold với nước n&oacute;ng g&acirc;y v&oacute;n cục, sử dụng liền sau khi pha,&nbsp;kh&ocirc;ng n&ecirc;n&nbsp;để l&acirc;u kh&ocirc;ng đảm bảo dinh dưỡng.</p>\r\n', '15980281744.jpg', 1, 1720000, 16),
(7, 1, 'ISO 100 5lbs', '<p>Dymatize Whey<strong>&nbsp;ISO 100</strong>&nbsp;l&agrave; thực phẩm bổ sung Whey Hydrolyzed hỗ trợ cơ bắp hiệu quả nhất, cam kết ch&iacute;nh h&atilde;ng, uy t&iacute;n v&agrave; gi&aacute; rẻ tại H&agrave; Nội</p>\r\n', '<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>1.Giới thi&ecirc;̣u v&ecirc;̀ ISO 100&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mục ti&ecirc;u của bạn l&agrave; c&oacute; một cơ thể săn chắc khỏe mạnh với những m&uacute;i cơ mang lại vẻ tự tin cho bạn mỗi khi bạn cởi &aacute;o khoe body. Nhưng lại cảm thấy kh&oacute; khăn trong việc bổ sung đủ nguồn protein&nbsp;để x&acirc;y dựng cơ bắp v&agrave; ho&agrave;n thiện cơ thể&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vậy c&ograve;n do dự g&igrave; nữa&nbsp;ISO 100&nbsp;chắc chắn l&agrave; một sự lựa chọn ho&agrave;n hảo gi&agrave;nh cho bạn, kh&ocirc;ng chỉ c&oacute; vậy&nbsp;ISO 100&nbsp;sẽ gi&uacute;p bạn cải thiện v&agrave; r&uacute;t ngắn thời gian để bạn đạt được kết quả một c&aacute;ch sớm nhất bởi nguồn protein&nbsp;tinh khiết m&agrave;&nbsp;ISO 100&nbsp;mang lại cho bạn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sản phẩm ISO 100 với nguồn Protein hấp thụ si&ecirc;u nhanh&nbsp;của h&atilde;ng&nbsp;Dymatize&nbsp;được rất nhiều vận động viện tin tưởng, chọn lựa v&agrave; sử dụng. Đơn giản v&igrave; ISO 100 c&oacute; tất cả những thứ bạn cần&nbsp;&nbsp;với mục đ&iacute;ch đẩy nhanh qu&aacute; tr&igrave;nh phục hồi, t&aacute;o tạo c&aacute;c m&ocirc; cơ đồng thời li&ecirc;n tục đẩy mạnh sản sinh tổng hợp Protein l&ecirc;n c&aacute;c m&ocirc; cơ mới.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>2. Th&agrave;nh phần, c&ocirc;ng dụng ISO 100 mang lại cho bạn.</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>▪️ Trong mỗi một muỗng ISO 100&nbsp;cung cấp tới 25 grams 100% Whey Protein Isolate đ&atilde; qua c&ocirc;ng nghệ thủy ph&acirc;n c&ugrave;ng với 5,5 grams chuỗi Amino acids ( BCCAs), chưa kể đến 2,7 grams BCAA L-Leucine-th&agrave;nh phần dưỡng chất dễ bị mất đi trong qu&aacute; tr&igrave;nh tập luyện mạnh.</p>\r\n\r\n<p>▪️ ISO 100 l&agrave; sản phẩm kh&ocirc;ng đi k&egrave;m Gluten v&agrave; Lactose c&oacute; chưa&nbsp;tới 1 gram đường, kh&ocirc;ng hề c&oacute; một ch&uacute;t chất b&eacute;o n&agrave;o trong&nbsp;mỗi khẩu phần.&nbsp;Người sử dụng ho&agrave;n to&agrave;n c&oacute; thể tin tưởng tin d&ugrave;ng m&agrave; kh&ocirc;ng lo sợ lượng chất b&eacute;o dư thừa c&oacute; thể ảnh hưởng đến qu&aacute; tr&igrave;nh luyện tập.</p>\r\n\r\n<p>▪️ Ưu điểm nhất của ISO 100 đ&oacute; ch&iacute;nh l&agrave; sản phẩm bổ sung nguồn Protein hấp thụ si&ecirc;u nhanh m&agrave; cơ thể bạn cần ngay lập tức trong 2 giai đoạn đ&oacute; ch&iacute;nh l&agrave; v&agrave;o buổi s&aacute;ng v&agrave; ngay sau khi tập&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&agrave;nh phần ch&iacute;nh của ISO 100 :&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>▪️&nbsp; 25g Hydrolyzed 100% Whey Protein Isolate ( Whey đ&atilde; thủy ph&acirc;n si&ecirc;u tinh khiết )&nbsp;</p>\r\n\r\n<p>▪️&nbsp; 5.5g BCAAs v&agrave; 2.7g L-Leucine gi&uacute;p chống dị h&oacute;a v&agrave; phục hồi cơ bắp&nbsp;</p>\r\n\r\n<p>▪️&nbsp; 0g Fat ( chất b&eacute;o ) v&agrave; 0g đường gi&uacute;p bạn kiếp so&aacute;t tốt việc calo nạp v&agrave;o cơ thể&nbsp;</p>\r\n\r\n<p>▪️&nbsp; Kh&ocirc;ng Gluten v&agrave; kh&ocirc;ng Lactose&nbsp;</p>\r\n\r\n<p>▪️&nbsp; Được th&ecirc;m bởi nhưng hương vị tự nhi&ecirc;n gi&uacute;p cảm nhận vị ngon hơn&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>3. Hướng dẫn sử dụng ISO 100 của Gymstore hi&ecirc;̣u quả&nbsp;</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sẽ c&oacute; 2 thời điểm quan trọng nhất trong ng&agrave;y m&agrave; cơ thể bạn cần Protein đ&oacute; ch&iacute;nh l&agrave; buổi s&aacute;ng sau khi ngủ dậy v&agrave; sau khi tập&nbsp;</p>\r\n\r\n<p>- V&agrave;o buổi s&aacute;ng:</p>\r\n\r\n<p>◾️ C&aacute;ch 1: Bạn pha một muỗng ISO 100 với 150g Quả Bơ với 100g Yến Mạch c&ugrave;ng với 300-500ml: Bạn sẽ c&oacute; đủ năng lương cho cả một buổi s&aacute;ng l&agrave;m việc&nbsp;</p>\r\n\r\n<p>◾️ C&aacute;ch 2: Đối với những người bận rộn : Bạn chỉ cần pha một muỗng ISO 100 với khoảng tầm 300ml nước m&aacute;t để sử dụng&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Ngay sau khi tập :</p>\r\n\r\n<p>◾️ Bạn pha một muỗng ISO 100 với 300ml nước . Sử dụng ngay sau khi tập bởi v&igrave; t&iacute;nh từ khoảng bạn tập xong đến 30p l&agrave; khoảng thời gian cơ thể bạn mở ra một c&aacute;nh cửa c&oacute; t&ecirc;n l&agrave;&nbsp;cửa sổ anabolic được cho l&agrave; thời điểm v&agrave;ng v&agrave; ho&agrave;n hảo nhất để bổ sung Protein gi&uacute;p x&acirc;y dựng một c&aacute;ch t&ocirc;t nhất.</p>\r\n', '15980289205.jpg', 1, 2300000, 18),
(8, 1, 'Impact My Protein', '<p>Thương hiệu Myprotein&nbsp;từ trước tới nay cực kỳ nổi tiếng tr&ecirc;n to&agrave;n thế giới, đ&acirc;y l&agrave; h&atilde;ng sản xuất thực phẩm bổ sung cho người tập gym với gi&aacute; th&agrave;nh tương đối rẻ. Đặc bi&ecirc;̣t đ&ocirc;́i với IMPACT WHEY ISOLATE là loại WHEY PROTEIN 100% Isolate nhưng v&acirc;̃n giữ được mức giá cạnh tranh nh&acirc;́t trong ph&acirc;n khúc Whey Protein cao c&acirc;́p&nbsp;</p>\r\n', '<h2>I. Giới thi&ecirc;̣u và thành ph&acirc;̀n của IMPACT WHEY ISOLATE&nbsp;</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>1. Giới thi&ecirc;̣u v&ecirc;̀ IMPACT WHEY ISOLATE - PHÁT TRI&Ecirc;̉N CƠ BẮP T&Ocirc;́T HƠN&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Impact Whey Isolate&nbsp;có hàm lượng Protein cao nh&acirc;́t trong m&ocirc;̃i l&acirc;̀n dùng ( 90% ) ngoài ra s&ocirc;́ lượng ch&acirc;́t béo và lactose là r&acirc;́t ít với các Axit Amin thi&ecirc;́t y&ecirc;́u và kh&ocirc;ng thi&ecirc;́t y&ecirc;́u nhằm tác đ&ocirc;̣ng b&ocirc;̉ sung Whey Isolate có giá trị sinh học cao nh&acirc;́t của t&acirc;́t cả các loại Protein&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Impact Whey Isolate&nbsp;được tinh lọc từ sữa b&ograve; v&agrave; cung cấp c&aacute;c Axit Amin thiết yếu: Axit Amin (EAA) v&agrave; chuỗi chi nh&aacute;nh (BCAAs) trong đ&oacute; đặc biệt quan trọng đối với việc k&iacute;ch th&iacute;ch tổng hợp Protein cơ (MPS), duy tr&igrave; v&agrave; x&acirc;y dựng c&aacute;c m&ocirc; cơ nạc sau luyện tập, hỗ trợ phục hồi cơ, đồng thời ngăn ngừa qu&aacute; tr&igrave;nh đốt ch&aacute;y cơ bắp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&ocirc;ng nghệ lọc Cross-Flow Micro-filtration</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Impact Whey Isolate&nbsp;sử dụng c&ocirc;ng nghệ lọc Cross-Flow Micro-filtration t&acirc;n tiến gi&uacute;p bẻ g&atilde;y chất b&eacute;o cholesterol v&agrave; lactose nhằm mang lại nguồn protein tinh khiết nhất c&oacute; thể.&nbsp;Impact Whey Isolate Myprotein, được l&agrave;m từ ph&ocirc; mai ngọt của những người n&ocirc;ng d&acirc;n, l&agrave; loại protein thuận tiện dễ d&agrave;ng nhất m&agrave; kh&ocirc;ng cần sự chuẩn bị của bất k&igrave; loại thực phẩm k&igrave; c&ocirc;ng n&agrave;o kh&aacute;c. Hầu hết mọi người đều nghiện c&aacute;c m&oacute;n ngọt như chocolate hoặc kẹo, cho d&ugrave; vậy, Vị ngọt của&nbsp;Impact Whey Isolate&nbsp;cam kết thỏa m&atilde;n người sử dụng bất kể l&agrave; kh&aacute;ch h&agrave;ng hảo ngọt nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>2. Thành ph&acirc;̀n của IMPACT WHEY ISOLATE&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&agrave;nh phần của Protein: 47% Beta-lactoglobulin, 17% Alpha-lactalbumin, 1.5% Bovine serum albumin,2% Immunoglobulin G, 0.5% Lactoferrin, 27% Glycomacropeptide.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Impact Whey Isolate&nbsp;chứa 100% Whey Protein Isolate trong th&agrave;nh phần. Mỗi lần d&ugrave;ng sẽ cung cấp 100% Protein Isolate gi&uacute;p tăng trưởng v&agrave; phục hồi khối lượng nạc ở mức cao nhất.​ K&ecirc;́t hợp với 5g BCAA với tỉ lệ 2:2:1 l&agrave; Leucine &ndash; Valine &ndash; Isoleucine giúp phục h&ocirc;̀i và ch&ocirc;́ng dị họa cơ bắp. Nhưng n&ecirc;́u bạn bi&ecirc;́t&nbsp;chỉ c&oacute; chuỗi Leucine&nbsp;mới đem lại hiểu quả cao cho cơ bắp t&ocirc;́i đa nh&acirc;́t và trong IMPACT WHEY ISOLATE có t&acirc;̣n&nbsp;2G&nbsp;Leucine h&ocirc;̃ trợ t&ocirc;́i đa vi&ecirc;̣c phát tri&ecirc;̉n cơ bắp t&ocirc;́t nh&acirc;́t.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>3. Lợi &iacute;ch ch&iacute;nh&nbsp;IMPACT WHEY ISOLATE mang tới cho bạn&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>▪️ Gi&aacute; trị dinh dưỡng ho&agrave;n to&agrave;n xứng đ&aacute;ng với số tiền bạn bỏ ra</p>\r\n\r\n<p>▪️ H&agrave;m lượng protein ~90%</p>\r\n\r\n<p>▪️ Protein hấp thụ nhanh ch&oacute;ng</p>\r\n\r\n<p>▪️ H&agrave;m lượng trong mỗi serving dao động từ 20-22g, t&ugrave;y v&agrave;o m&ugrave;i vị</p>\r\n\r\n<p>▪️ &Iacute;t chất b&eacute;o v&agrave; carbohydrate</p>\r\n\r\n<p>▪️ Lu&ocirc;n đứng vị tr&iacute; TOP Đầu của chứng nhận Labdoor</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>4. Hướng d&acirc;̃n sử dụng&nbsp;IMPACT WHEY ISOLATE hi&ecirc;̣u quả nh&acirc;́t&nbsp;</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thời đi&ecirc;̉m t&ocirc;́t nh&acirc;́t sử dụng đó chính là bu&ocirc;̉i sáng sau khi ngủ d&acirc;̣y và thời đi&ecirc;̉m vàng phát tri&ecirc;̉n cơ t&ocirc;́t nh&acirc;́t đó chính là sau khi t&acirc;̣p. M&ocirc;̃i l&acirc;̀n sử dụng pha 1-2 mu&ocirc;̃ng IMPACT WHEY ISOLATE với 250-300ml nước sử dụng ngay và lu&ocirc;n đ&ecirc;̉ có được m&ocirc;̣t k&ecirc;́t quả t&ocirc;́t nh&acirc;́t.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '15981568226.jpg', 1, 2700000, 15),
(9, 1, 'Whey Gold 10lbs', '<p>Trong su&ocirc;́t 9 năm li&ecirc;n ti&ecirc;́p, Whey Gold là dòng Whey Protein bán chạy nh&acirc;́t tr&ecirc;n th&ecirc;́ giới dựa tr&ecirc;n trang Bodybuilding.com. Đ&ecirc;̉ khẳng định m&ocirc;̣t ti&ecirc;u chu&acirc;̉n vàng qua từng năm tháng. Whey Gold Standard v&acirc;̃n lu&ocirc;n là dòng Whey Protein được mọi người bi&ecirc;́t đ&ecirc;́n nhi&ecirc;̀u nh&acirc;́t từ trước tới nay.</p>\r\n', '<h2 style=\"text-align:start\"><span style=\"font-size:20px\"><span style=\"font-family:brandon_textbold\"><span style=\"color:#000000\"><span style=\"font-size:20px\"><span style=\"color:#c3c3c3\"><span style=\"font-size:20px\"><span style=\"color:#e9224a\"><span style=\"font-size:18px\"><span style=\"font-size:18px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">I. Giới thi&ecirc;̣u và thành ph&acirc;̀n của Whey Gold Standard&nbsp;</span></span></span></span></span></span></span></span></span></span></h2>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:brandon_textregular\">&nbsp;</span></span></span></p>\r\n\r\n<h3 style=\"text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:brandon_textbold\"><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"color:#c3c3c3\"><span style=\"font-size:16px\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:16px\"><span style=\"color:#000000\">1. Giới thi&ecirc;̣u v&ecirc;̀ Whey Gold Standard&nbsp;</span></span></span></span></span></span></span></span></span></span></h3>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:brandon_textregular\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:brandon_textregular\"><span style=\"font-size:14px\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:14px\"><span style=\"color:#e9224a\">Whey Gold Standard</span></span><span style=\"color:#000000\">&nbsp;là dòng sản ph&acirc;̉m Whey Protein chủ đạo của hãng Optinum Nutrition. Đ&ocirc;̀ng thời cũng đang là sản ph&acirc;̉m bán chạy nh&acirc;́t tr&ecirc;n thị trường cả ở trong nước l&acirc;̃n ngoài nước.&nbsp;</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:brandon_textregular\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:brandon_textregular\"><span style=\"font-size:14px\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">Sự pha tr&ocirc;̣n hoàn hảo giữa 2 ngu&ocirc;̀n Protein đó chính là Whey Protein Concentrate và Whey Protein Isolate. Trong đó tỉ l&ecirc;̣ Whey Protein Isolate chi&ecirc;́m nhi&ecirc;̀u nh&acirc;́t. Đã từ r&acirc;́t l&acirc;u, Whey Gold Standard đã là&nbsp;</span><span style=\"font-size:14px\"><span style=\"color:#e9224a\">TI&Ecirc;U CHU&Acirc;̉N VÀNG</span></span><span style=\"color:#000000\">&nbsp;đ&ecirc;̉ phục vụ cho nhu c&acirc;̀u tăng cơ giảm mỡ của r&acirc;́t nhi&ecirc;̀u người. Kh&ocirc;ng chỉ nói với những ai đang t&acirc;̣p th&ecirc;̉ hình mà k&ecirc;̉ cả đ&ocirc;́i với những người đang mu&ocirc;́n&nbsp;tìm m&ocirc;̣t ngu&ocirc;̀n đạm ( Whey Protein ) đ&ecirc;̉ cung c&acirc;́p dinh dưỡng cho cơ th&ecirc;̉ đ&ecirc;̀u sẽ chọn lựa đ&ecirc;́n&nbsp;</span><span style=\"font-size:14px\"><span style=\"color:#e9224a\">Whey Gold Standard</span></span><span style=\"color:#000000\">&nbsp;đ&acirc;̀u ti&ecirc;n.&nbsp;</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:brandon_textregular\">&nbsp;</span></span></span></p>\r\n\r\n<h3 style=\"text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:brandon_textbold\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-size:16px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"color:#000000\">2. Thành ph&acirc;̀n của Whey Gold Standard&nbsp;</span></span></span></span></span></span></span></h3>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:brandon_textregular\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:brandon_textregular\"><span style=\"font-size:14px\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><span style=\"font-size:14px\"><span style=\"color:#000000\">Whey Protein l&agrave; th&agrave;nh phần chủ yếu trong sản phẩm sữa tăng cơ&nbsp;</span></span><span style=\"font-size:14px\"><span style=\"color:#e9224a\">Whey Gold Standard&nbsp;</span></span><span style=\"font-size:14px\"><span style=\"color:#000000\">v&agrave; ngo&agrave;i ra, c&ograve;n một số chất dinh dưỡng kh&aacute;c l&agrave;: 140 calo, 24G Protein , 1g chất b&eacute;o, 3 carb, 1g đường, 5.5g&nbsp;</span></span><a href=\"https://www.wheystore.vn/c161/amino-acid\" style=\"box-sizing:border-box; color:#000000; text-decoration:none; outline:0px; border:0px; font-size:14px; padding:0px; vertical-align:baseline; transition:all 0.3s ease-in-out 0s\"><span style=\"font-size:14px\"><span style=\"color:#000000\">BCAA</span></span></a><span style=\"font-size:14px\"><span style=\"color:#000000\">, 4g glutamine v&agrave; axit glutamic trong mỗi muỗng.</span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:brandon_textregular\"><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\">Ngo&agrave;i ra, trong Whey Gold c&ograve;n bổ sung th&ecirc;m Lastase &ndash; một loại enzyme gi&uacute;p ti&ecirc;u h&oacute;a lactose trong Whey Protein Concentrate.</span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\">&nbsp;</p>\r\n\r\n<h2>II. Tác dụng, ưu đi&ecirc;̉m và cách sử dụng Whey Gold Standard&nbsp;</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>1. Tác dụng của Whey Gold Standard&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>▪️ Cung c&acirc;́p m&ocirc;̣t ngu&ocirc;̀n Protein ch&acirc;́t lượng cao giúp bạn tăng cơ t&ocirc;́t hơn&nbsp;</p>\r\n\r\n<p>▪️ Phục h&ocirc;̀i cơ bắp m&ocirc;̣t cách nhanh nh&acirc;́t có th&ecirc;̉</p>\r\n\r\n<p>▪️ Ki&ecirc;̉m soát ch&ecirc;́ đ&ocirc;̣ ăn d&ecirc;̃ dàng và ti&ecirc;̣n lợi&nbsp;</p>\r\n\r\n<p>▪️ Giúp bạn tăng trao đ&ocirc;̉i ch&acirc;́t t&ocirc;́t hơn trong cơ th&ecirc;̉</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>2. Ưu đi&ecirc;̉m của Whey Gold Standard&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;▪️ V&ecirc;̀ mùi vị : Hãng đã sản xu&acirc;́t r&acirc;́t nhi&ecirc;̀u mùi vị khác nhau giúp bạn lựa chọn d&ecirc;̃ dàng cụ th&ecirc;̉ như:&nbsp;vani, chocolate, d&acirc;u t&acirc;y, kem chuối, &hellip; V&agrave; gần đ&acirc;y h&atilde;ng cho ra một số hương vị pha trộn mới độc đ&aacute;o l&agrave; Cookies &amp; Cream, Extreme Milk Chocolate, Mocha Capuchino, Double Rich Chocolate, Vanila Ice Cream, &hellip;</p>\r\n\r\n<p>▪️ V&ecirc;̀ khả năng hòa tan :&nbsp;&nbsp;được sản xuất cực kỳ tinh tế, kh&ocirc;ng c&oacute; chất phụ gia, bột mịn, kh&ocirc;ng bị v&oacute;n cục. Với đặc t&iacute;nh dễ h&ograve;a tan trong nức dễ d&agrave;ng với b&igrave;nh lắc hoặc m&aacute;y xay sinh tố v&agrave; kh&ocirc;ng c&oacute; qu&aacute; nhiều bọt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>3. Cách sử dụng Whey Gold Standard hi&ecirc;̣u quả nh&acirc;́t</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để đạt hiệu quả tối đa khi sử dụng&nbsp;Whey Gold Standard&nbsp;c&aacute;c bạn n&ecirc;n uống:</p>\r\n\r\n<ul>\r\n	<li>S&aacute;ng 1 lần khi ngủ dậy.</li>\r\n	<li>Uống sau tập 1 lần ( Thời gian quan trọng nh&acirc;́t giúp bạn h&acirc;́p thụ Protein t&ocirc;́t nh&acirc;́t )&nbsp;</li>\r\n</ul>\r\n\r\n<p>Lưu &yacute;: Mỗi một lần pha 1 muống với 300ml nước lạnh v&agrave; uống lu&ocirc;n sau khi pha. Kh&ocirc;ng n&ecirc;n pha sẵn rồi để tủ lạnh sẽ l&agrave;m mất độ chất của sản phẩm! Bạn cũng c&oacute; thể kết hợp với sữa tươi kh&ocirc;ng c&oacute; đường để tạo hương vị.</p>\r\n', '15981568297.jpg', 1, 2300000, 14),
(10, 1, 'ISO HD 5lbs', '<p>BPI SPORTS: C&ocirc;ng ty của chúng t&ocirc;i kh&ocirc;ng chỉ l&agrave; m&ocirc;̣t c&ocirc;ng ty chuy&ecirc;n về dinh dưỡng m&agrave; c&ograve;n l&agrave; c&ocirc;ng ty đứng đầu trong lĩnh vực sức khỏe v&agrave; thể h&igrave;nh. BPI SPORTS tin rằng th&agrave;nh phần dinh dưỡng tốt nhất tạo n&ecirc;n một sản phẩm chất lượng.</p>\r\n', '<h2>I. Giới thi&ecirc;̣u v&ecirc;̀ BPI&nbsp;ISO HD - 100% Whey Protein Isolate - Tăng cơ bắp t&ocirc;́i đa&nbsp;</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>1. Sản ph&acirc;̉m của BPI SPORTS - ISO HD được c&acirc;́p chứng chỉ từ trung t&acirc;m ki&ecirc;̉m nghi&ecirc;̣m ch&acirc;́t lượng sản ph&acirc;̉m lớn nh&acirc;́t nước Mỹ ( ChromaDex )&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tại&nbsp;BPI Sport, ch&uacute;ng t&ocirc;i cam kết mang lại cho bạn những sản phẩm dinh dưỡng thể thao&nbsp;chất lượng cao nhất tuyệt đối. Nhưng b&acirc;y giờ sản phẩm của ch&uacute;ng t&ocirc;i kh&ocirc;ng phải chỉ đến từ những lời giới thiệu. Ch&uacute;ng t&ocirc;i đang đặt tất cả c&aacute;c protein của ch&uacute;ng t&ocirc;i th&ocirc;ng qua thử nghiệm nghi&ecirc;m ngặt bởi&nbsp;ChromaDex&reg;, một ph&ograve;ng th&iacute; nghiệm của&nbsp;b&ecirc;n thứ 3, v&agrave; l&agrave; một trong những c&aacute;i t&ecirc;n được k&iacute;nh trọng nhất trong n&ecirc;n&nbsp;Dinh dưỡng thể thao Hoa Kỳ. C&aacute;c dấu&nbsp;ChromaDex&reg;&nbsp;Chất lượng được x&aacute;c minh c&oacute; nghĩa l&agrave; kh&ocirc;ng c&oacute; protein &quot;v&ocirc; nghĩa&#39;&quot; v&agrave; khẳng định rằng sản phẩm đ&aacute;p ứng đặc điểm kỹ thuật cho sắc tố, chất g&acirc;y &ocirc; nhiễm, c&aacute;c kim loại nặng v&agrave; vi thể.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&oacute; phục vụ như bằng chứng kh&aacute;ch quan rằng c&aacute;c ngu&ocirc;̀n protein của ch&uacute;ng t&ocirc;i l&agrave; ph&ugrave; hợp với&nbsp;cGMP &nbsp;(Q&uacute;a tr&igrave;nh tr&igrave;nh sản xuất Tốt nhất)&nbsp;v&agrave; rằng những g&igrave; bạn thấy được liệt k&ecirc; trong th&agrave;nh phần của ch&uacute;ng t&ocirc;i l&agrave; ch&iacute;nh x&aacute;c những g&igrave; l&agrave; trong c&aacute;c sản phẩm của ch&uacute;ng t&ocirc;i. Kh&ocirc;ng c&oacute; g&igrave; &iacute;t hơn, kh&ocirc;ng c&oacute; g&igrave; nhiều&nbsp;hơn. - H&Atilde;Y TIN NHỮNG G&Igrave; BẠN CẢM THẤY&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>2. BPI ISO HD L&Agrave; G&Igrave;?</h3>\r\n\r\n<p><br />\r\nISO HD l&agrave; whey&nbsp;protein calo thấp, tinh khiết nhất cung cấp 25 gram Protein&nbsp;si&ecirc;u cao cấp&nbsp;100% Whey Protein Isolate&nbsp;trong m&ocirc;̃i kh&acirc;̉u ph&acirc;̀n.&nbsp;Dạng protein n&agrave;y hấp thụ nhanh, khiến n&oacute; trở th&agrave;nh c&ocirc;ng cụ đắc lực nạp năng lượng cho cơ bắp sau qu&aacute; tr&igrave;nh tập luyện-khoảng thời gian quan trọng nhất để nạp dinh dưỡng nu&ocirc;i dưỡng ph&aacute;t triển cơ bắp. ISO HD bổ trợ sức khỏe, t&aacute;i tạo v&agrave; ph&aacute;t triển cơ bắp tối ưu.</p>\r\n\r\n<h2>II. Thành ph&acirc;̀n và những lợi ích của BPI ISO HD&nbsp;</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>1. NHỮNG TH&Agrave;NH PHẦN TRONG BPI ISO HD L&Agrave;M&nbsp;ĐƯỢC G&Igrave;?</h3>\r\n\r\n<p><br />\r\n100% Whey Protein C&ocirc; lập:<br />\r\nMột whey hấp thụ nhanh ch&oacute;ng (v&igrave; độ tinh khiết của n&oacute;) m&agrave; c&aacute;c sợi cơ&nbsp;c&oacute; thể tổng hợp&nbsp;được&nbsp;nhiều Protein hơn.</p>\r\n\r\n<p>Protein Hydrolysate:<br />\r\nViệc ti&ecirc;u thụ&nbsp;protein thủy ph&acirc;n&nbsp;cho ph&eacute;p c&aacute;c axit amin được hấp thu v&agrave;o cơ thể với một tốc độ nhanh hơn do đ&oacute; tối đa h&oacute;a c&aacute;c chất dinh dưỡng được cung cấp cho c&aacute;c m&ocirc; cơ cho việc phục hồi v&agrave; tăng trưởng - đặc&nbsp;biệt l&agrave; sau khi tập luyện.&nbsp;</p>\r\n\r\n<h3>2. BPI ISO HD&nbsp;C&Oacute; LỢI NHƯ N&Agrave;O?&nbsp;</h3>\r\n\r\n<p><br />\r\nISO HD &trade; được hấp thu nhanh ch&oacute;ng trong cơ thể tiếp tục hỗ trợ phục hồi sau khi tập luyện v&agrave; x&acirc;y dựng cơ bắp tăng tốc. H&agrave;m lượng&nbsp;canxi cao v&agrave; h&agrave;m lượng Natri thấp, c&ocirc;ng thức n&agrave;y l&agrave; chưa từng c&oacute; kh&ocirc;ng chỉ c&oacute; gi&aacute; trị dinh dưỡng m&agrave; c&ograve;n l&agrave;m gia tăng&nbsp;hương vị.</p>\r\n\r\n<p>▪️ Thúc đ&acirc;̉y phát tri&ecirc;̉n cơ bắp t&ocirc;́i ưu&nbsp;</p>\r\n\r\n<p>▪️ Phục h&ocirc;̀i nhanh chóng sau t&acirc;̣p luy&ecirc;̣n&nbsp;</p>\r\n\r\n<p>▪️ H&ocirc;̃ trợ giảm mỡ và ki&ecirc;̉m soát ch&ecirc;́ đ&ocirc;̣ dinh dưỡng&nbsp;</p>\r\n\r\n<p>▪️ Hương vị thơm ngon, d&ecirc;̃ dàng pha ch&ecirc;́&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>3. Hướng d&acirc;̃n sử dụng của BPI ISO HD&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để đạt hiệu quả tối đa khi sử dụng&nbsp;BPI ISO HD<strong>,</strong>&nbsp;c&aacute;c bạn n&ecirc;n uống:</p>\r\n\r\n<ul>\r\n	<li>S&aacute;ng 1 lần khi ngủ dậy.</li>\r\n	<li>Uống sau tập 1 lần ( Thời gian quan trọng nh&acirc;́t giúp bạn h&acirc;́p thụ Protein t&ocirc;́t nh&acirc;́t )&nbsp;</li>\r\n</ul>\r\n\r\n<p>Lưu &yacute;: Mỗi một lần pha 1 muống với 300ml nước lạnh v&agrave; uống lu&ocirc;n sau khi pha. Kh&ocirc;ng n&ecirc;n pha sẵn rồi để tủ lạnh sẽ l&agrave;m mất độ chất của sản phẩm! Bạn cũng c&oacute; thể kết hợp với sữa tươi kh&ocirc;ng c&oacute; đường để tạo hương vị.</p>\r\n', '15981568408.jpg', 1, 1600000, 22),
(11, 2, 'Serious Mass Gainer', '<p>Serious Mass l&agrave; dòng&nbsp;sữa tăng c&acirc;n nổi tiếng v&agrave; b&aacute;n chạy nhất thể giới với đã hơn 30 năm ph&aacute;t triển tr&ecirc;n to&agrave;n cầu. Optinum Serious Mass có th&ecirc;̉ nói lu&ocirc;n&nbsp;l&agrave; sự&nbsp;lựa chọn gi&agrave;nh cho người c&oacute; nhu cầu tăng c&acirc;n v&agrave; tăng cơ nhanh. Sản phẩm được sản xu&acirc;́t&nbsp;theo d&acirc;y chuyền hiện đại đ&ecirc;̉&nbsp;đ&aacute;p ứng nhu cầu cấp thiết về việc tăng c&acirc;n.&nbsp;</p>\r\n', '<h2>I. Những đi&ecirc;̉m n&ocirc;̉i b&acirc;̣t&nbsp;của Serious Mass - Sữa Tăng C&acirc;n Nhanh&nbsp;</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>1. Giới thi&ecirc;̣u v&ecirc;̀ Serious Mass</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Serious Mass&nbsp;có th&ecirc;̉ nói là 1&nbsp;sản ph&acirc;̉m chủ đạo của hãng OPTIMUM NUTRITION ( ON ). Với sự phát tri&ecirc;̉n và c&ocirc;́ găng n&ocirc;̃ lực h&ecirc;́t mình trong hơn&nbsp;30 năm trong ngành ngh&ecirc;̀ thực ph&acirc;̉m b&ocirc;̉ sung. Hãng ON đã cho ra r&acirc;́t nhi&ecirc;̀u sản ph&acirc;̉m ch&acirc;́t lượng như&nbsp;<a href=\"https://gymstore.vn/on-gold-standard-100-whey-5-lbs-2-27-kg\">WHEY GOLD</a>&nbsp;hoặc&nbsp;<a href=\"https://gymstore.vn/on-platinum-hydrowhey-3-5-lbs-1-59-kg\">PLATINUM HYDROWHEY</a>&nbsp;đ&ecirc;̀u là những sản ph&acirc;̉m ch&acirc;́t lượng phục vụ nhu c&acirc;̀u tăng cơ bắp. Ngoài ra hãng đã phát tri&ecirc;̉n m&ocirc;̣t sản ph&acirc;̉m sữa tăng c&acirc;n th&ecirc;́ h&ecirc;̣ mới mang đủ những đi&ecirc;̀u ki&ecirc;̣n c&acirc;̀n và đủ đ&ecirc;̉ giúp bạn tăng c&acirc;n t&ocirc;́t hơn đó chính là&nbsp;SERIOUS MASS 12lbs&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>2. Đi&ecirc;̉m mạnh của Serious Mass&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Điểm mạnh của&nbsp;ON Serious Mass&nbsp;l&agrave; bổ sung một lượng Vitamin thiết yếu, m&agrave; trong c&aacute;c loại sữa tăng c&acirc;n th&ocirc;ng thường kh&ocirc;ng c&oacute; được. Trong qu&aacute; tr&igrave;nh tăng c&acirc;n, Vitamin đ&oacute;ng vai tr&ograve; quan trọng trong việc hỗ trợ qu&aacute; tr&igrave;nh ti&ecirc;u ho&aacute;, hấp thu v&agrave; xử l&yacute; thức ăn, Vitamin cũng tham gia v&agrave;o nhiều q&uacute;a tr&igrave;nh trao đổi chất trong cơ thể. Việc Serious Mass bổ sung Vitamin đ&oacute; l&agrave; một ưu điểm cực lớn.</p>\r\n\r\n<h2>II. Thành ph&acirc;̀n và cách sử dụng của Serious Mass</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h3>1. Thành ph&acirc;̀n của Serious Mass&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Serious Mass cung cấp ???????????????? ???????????????????????????????? cho m&ocirc;̃i m&ocirc;̣t Serving sử dụng&nbsp;</p>\r\n\r\n<p>Có chứa tới&nbsp;???????????? ???????????????????????????? + ???????????????? ???????????????? c&ugrave;ng 25 loại ???????????????????????????? v&agrave; Kho&aacute;ng chất cần thiết cho qu&aacute; tr&igrave;nh tăng c&acirc;n th&agrave;nh c&ocirc;ng</p>\r\n\r\n<p>Cùng với vi&ecirc;̣c c&acirc;́u tạo b&ocirc;̣t được sản xu&acirc;́t đ&ecirc;̉ d&ecirc;̃ dàng hòa tan nh&acirc;́t chỉ qua m&ocirc;̣t vài cái lắc nhẹ. Đi kèm với đó chính là hương vị của Serious Mass kh&ocirc;ng h&ecirc;̀ khó u&ocirc;́ng th&acirc;̣m chí còn r&acirc;́t d&ecirc;̃ u&ocirc;́ng k&ecirc;̉ cả đ&ocirc;́i với nhưng người đ&acirc;̀u ti&ecirc;n sử dụng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>2. Cách sử dụng của Serious Mass hi&ecirc;̣u quả nh&acirc;́t</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đối với người mới bắt đầu sử dụng,&nbsp;Tuần đầu ti&ecirc;n n&ecirc;n c&oacute; giai đoạn l&agrave;m quen với sản phẩm:&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;D&ugrave;ng 1 muỗng chia l&agrave;m 2 hoặc 3 lần sử dụng trong ng&agrave;y, mỗi lần&nbsp;1/3 muỗng (theo vạch chia) với 200 ml nước đun s&ocirc;i để nguội, hoặc nước lạnh, sữa tươi hoặc nước hoa quả.</p>\r\n\r\n<ul>\r\n	<li>Lần 1: 1/3 muỗng sau ăn s&aacute;ng 1 giờ.</li>\r\n	<li>Lần 2:&nbsp;1/3 muỗng v&agrave;o thời điểm giữa buổi chiều, sau ăn trưa tối thiểu 1 tiếng v&agrave; trước tập 1 tiếng.</li>\r\n	<li>Lần 3:&nbsp;1/3 muỗng ngay sau tập.</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; Từ tuần thứ 2, bắt đầu tăng l&ecirc;n với 2 muỗng/ng&agrave;y.&nbsp;Chia l&agrave;m 2 hoặc 3 lần sử dụng trong ng&agrave;y, mỗi lần&nbsp;2/3 muỗng (theo vạch chia) với 400 ml nước đun s&ocirc;i để nguội, hoặc nước lạnh, sữa tươi hoặc nước hoa quả.</p>\r\n\r\n<ul>\r\n	<li>Lần 1: 2/3 muỗng sau ăn s&aacute;ng 1 giờ.</li>\r\n	<li>Lần 2:&nbsp;2/3 muỗng v&agrave;o thời điểm giữa buổi chiều, sau ăn trưa tối thiểu 1 tiếng v&agrave; trước tập 1 tiếng.</li>\r\n	<li>Lần 3:&nbsp;2/3 muỗng ngay sau tập.</li>\r\n</ul>\r\n\r\n<p><strong><em>Lưu &nbsp;&yacute;:</em></strong></p>\r\n\r\n<p>- N&ecirc;n lắc bằng b&igrave;nh lắc chuy&ecirc;n dụng, hoặc m&aacute;y xay sinh tố mới đảm bảo độ tan của sản phẩm.</p>\r\n\r\n<p>- Kh&ocirc;ng pha với nước n&oacute;ng.</p>\r\n\r\n<p>- Sử dụng ngay sau khi pha, kh&ocirc;ng để l&acirc;u, để tủ lạnh t&iacute;ch trữ.</p>\r\n\r\n<p>- Duy tr&igrave; chế độ ăn như trước kh&iacute; sử dụng, hoặc ăn bổ sung nếu c&oacute; thể sẽ gi&uacute;p tăng c&acirc;n tốt hơn.</p>\r\n\r\n<p>- Sau khi tăng c&acirc;n đủ, m&agrave; kh&ocirc;ng sử dụng sản phẩm, sẽ kh&ocirc;ng bị giảm c&acirc;n nếu bạn ăn đầy đủ b&ugrave; th&ecirc;m dinh dưỡng cho cơ thể.</p>\r\n', '15981568479.jpg', 1, 1500000, 10);
INSERT INTO `products` (`id`, `category_id`, `name`, `description`, `content`, `photo`, `hot`, `price`, `discount`) VALUES
(12, 2, 'Mucsle Mass Gainer', '<p>Labrada Muscle Mass Gainer l&agrave; sản phẩm sữa tăng c&acirc;n được y&ecirc;u th&iacute;ch h&agrave;ng đầu v&igrave; khả năng tăng c&acirc;n nhanh ch&oacute;ng, đồng thời hỗ trợ x&acirc;y dựng cơ bắp tối ưu trong suốt qu&aacute; tr&igrave;nh sử dụng nhờ th&agrave;nh phần v&agrave; tỷ lệ dinh dưỡng chuẩn mực. Nếu bạn l&agrave; người gầy kinh ni&ecirc;n, kh&oacute; tăng c&acirc;n, hấp thụ k&eacute;m,... th&igrave; đ&acirc;y ch&iacute;nh l&agrave; sản phẩm bạn cần t&igrave;m!</p>\r\n', '<p><strong>Th&agrave;nh phần ch&iacute;nh của Muscle Mass Gainer :&nbsp;</strong></p>\r\n\r\n<p><strong>Sữa tăng c&acirc;n</strong><strong>&nbsp;Muscle Mass Gainer</strong>&nbsp;với c&aacute;c chỉ số&nbsp; đứng đầu c&aacute;c d&ograve;ng sữa tăng c&acirc;n, c&ugrave;ng đến với những chỉ số của mỗi th&agrave;nh phần để ngay cả những người mới tiếp cận đến c&aacute;c thực phẩm bổ sung cũng hiểu r&otilde; được. Mỗi lần d&ugrave;ng lớn bao gồm 9 muỗng khi pha k&egrave;m sữa cung cấp :</p>\r\n\r\n<ul>\r\n	<li>1930 calories một lần d&ugrave;ng: tăng c&acirc;n&nbsp; tăng cơ dễ d&agrave;ng(<a href=\"https://pubmed.ncbi.nlm.nih.gov/14672296/\" rel=\"nofollow noopener\" target=\"_blank\">1</a>).</li>\r\n	<li>84g Protein phức hợp : gi&agrave;u&nbsp; protein hỗ trợ ph&aacute;t triển cơ bắp hiệu quả.</li>\r\n	<li>315g carb: lượng tinh bột g&oacute;p phần tạo ra năng lượng dồi d&agrave;o đẩy nhanh qu&aacute; tr&igrave;nh tăng c&acirc;n.</li>\r\n	<li>17g BCAA + Creatine : phục hồi v&agrave; ph&aacute;t triển cơ bắp, chống dị h&oacute;a cơ.</li>\r\n	<li>Bổ sung Vitamin, kho&aacute;ng chất thiết yếu cho cơ bắp, hỗ trợ hấp thu dinh dưỡng hiệu quả.</li>\r\n</ul>\r\n\r\n<h3><strong>Lợi &iacute;ch vượt trội của Labrada Muscle Mass :</strong></h3>\r\n\r\n<ul>\r\n	<li>Độ mịn nhỏ dễ h&ograve;a tan, Muscle Mass Gainer&nbsp; c&ograve;n sở hữu m&ugrave;i vị thơm ngon kh&aacute;c với c&aacute;c d&ograve;ng sữa tăng c&acirc;n kh&aacute;c vừa v&oacute;n cục vưa kh&oacute; uống. Ngo&agrave;i ra hệ ti&ecirc;u h&oacute;a vẫn giữ được ổn định khi sử dụng sản phẩm, kh&ocirc;ng bị đầy hơi hoặc rối loạn ti&ecirc;u h&oacute;a.</li>\r\n	<li>Trong một lần d&ugrave;ng,&nbsp; h&agrave;m lượng protein cực cao gi&uacute;p cơ thể nạp đầy đủ lượng protein cần thiết để x&acirc;y dựng v&agrave; ph&aacute;t triển cơ bắp. Sản phẩm c&oacute; cả một lượng tương đối nhiều BCAA gi&uacute;p hỗ trợ phục hồi một c&aacute;ch hiệu quả sau giờ tập luyện, chống dị h&oacute;a cơ.</li>\r\n	<li>Th&agrave;nh phần của Muscle Mass Gainer kh&ocirc;ng c&oacute; sự tham gia của&nbsp; c&aacute;c tinh bột k&eacute;m chất lượng cực kỳ&nbsp; đảm bảo an to&agrave;n cho anh em sử dụng. Hạn chế tối ưu việc t&iacute;ch nước, t&iacute;ch mỡ thừa cho cơ thể, giảm thiểu được c&aacute;c bệnh nguy hiểm cho con người như tiểu đường, mỡ m&aacute;u&hellip;</li>\r\n	<li>Bổ sung th&ecirc;m c&aacute;c Vitamin v&agrave; dưỡng chất c&oacute; trong Muscle Mass Gainer. V&igrave; Vitamin&nbsp; đ&oacute;ng vai tr&ograve; thiết yếu để duy tr&igrave; sự sống, tăng cường hệ miễn dịch, tham gia c&aacute;c hoạt động điều h&ograve;a của hệ thần kinh v&agrave; rất nhiều c&aacute;c lợi &iacute;ch kh&aacute;c cho sức khỏe. M&agrave; lượng vitamin c&oacute; trong cơ thể với kh&aacute; &iacute;t n&ecirc;n khi dung nạp từ Muscle Mass Gainer cơ thể bạn đ&atilde; được nạp&nbsp; một lượng&nbsp; vitamin dồi d&agrave;o qu&aacute; đủ cho cơ thể sử dụng</li>\r\n</ul>\r\n\r\n<p>C&aacute;c th&agrave;nh phần c&oacute; trong&nbsp;<em>Labrada Muscle Mass Gainer&nbsp;</em>đều chất lượng v&agrave; an to&agrave;n ph&ugrave; hợp với mọi người sử dụng&nbsp; v&agrave; đặc biệt ph&ugrave; hợp với:</p>\r\n\r\n<ul>\r\n	<li>Người kh&oacute; tăng c&acirc;n, gầy l&acirc;u năm, tập kh&ocirc;ng l&ecirc;n c&acirc;n ( sản phẩm được sản xuất với c&ocirc;ng nghệ ti&ecirc;n tiến ho&agrave;n to&agrave;n gi&uacute;p&nbsp; c&aacute;c bạn gầy kinh ni&ecirc;n tăng c&acirc;n hiệu quả.)</li>\r\n	<li>Vận động vi&ecirc;n thể h&igrave;nh trong giai đoạn xả cơ&nbsp; sau thi đấu (kết th&uacute;c m&ugrave;a giải c&aacute;c vận động vi&ecirc;n cần bổ sung lại lượng carb đ&atilde; cắt giảm v&agrave; c&aacute;c dưỡng chất kh&aacute;c, Muscle Mass Gainer cung cấp đủ c&aacute;c yếu tố đ&oacute; ph&ugrave; hợp để xả trở lại)</li>\r\n	<li>Người tập gym, r&egrave;n luyện c&aacute;c m&ocirc;n thể thao&hellip;,&nbsp; (c&aacute;c bạn r&egrave;n luyện thể chất đều muốn tăng c&acirc;n tăng cơ để dần chạm đến mục ti&ecirc;u bản th&acirc;n đặt ra, m&agrave; hiệu quả tăng c&acirc;n của sản phẩm n&agrave;y đ&atilde; qu&aacute; r&otilde; r&agrave;ng)</li>\r\n	<li>Người gi&agrave;, người bệnh hấp thu k&eacute;m, khả năng ăn uống hạn chế. (Muscle Mass Gainer sử dụng ph&ugrave; hợp, vị ngon dễ uống&nbsp; v&agrave; an to&agrave;n cho hệ ti&ecirc;u h&oacute;a, kh&ocirc;ng đầy hơi chướng bụng).</li>\r\n</ul>\r\n\r\n<h3><strong>C&aacute;ch sử dụng Muscle Mass Gainer hiệu quả :</strong></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đối với c&aacute;c bạn mới sử dụng, tuần đầu ti&ecirc;n :</p>\r\n\r\n<p>Sử dụng từ 4-6 muỗng chia ra l&agrave;m 4 lần sử dụng trong ng&agrave;y. Mỗi lần pha với nước nguội hoặc sữa tươi kh&ocirc;ng đường v&agrave; sử dụng liền, tr&aacute;nh để l&acirc;u mất chất dinh dưỡng.</p>\r\n\r\n<p>Thời điểm sử dụng:&nbsp;</p>\r\n\r\n<ul>\r\n	<li>S&aacute;ng sau khi ngủ dậy :&nbsp;Bởi v&igrave; buổi s&aacute;ng l&agrave; thời điểm th&iacute;ch hợp nhất để hấp thụ năng lượng, cơ thể rất cần protein sau một đ&ecirc;m đ&atilde; ti&ecirc;u thụ hết. Bổ sung một serving Muscle Mass Gainer l&uacute;c n&agrave;y với h&agrave;m lượng dưỡng chất cao sẽ gi&uacute;p bạn tr&agrave;n đầy năng lượng cho ng&agrave;y mới l&agrave;m việc.</li>\r\n	<li>Trước tập 60-90 ph&uacute;t :&nbsp;Việc bổ sung protein trước nửa tiếng, carb (tinh bột)&nbsp; trước 1 giờ chuẩn bị tập luyện đ&atilde; được c&aacute;c chuy&ecirc;n gia thể h&igrave;nh nghi&ecirc;n cứu kỹ lưỡng, khi đ&oacute; cơ thể đ&atilde; nạp đủ năng lượng cần thiết để bước v&agrave;o buổi tập.</li>\r\n	<li>Ngay sau tập :&nbsp;Sau buổi tập l&uacute;c n&agrave;y cơ bắp cần được phục hồi n&ecirc;n cần bổ sung một lượng lớn carb v&agrave; protein hấp thụ nhanh để cơ thể hấp thụ nu&ocirc;i dưỡng cơ bắp.</li>\r\n	<li>Trước khi ngủ :&nbsp;Qu&aacute; tr&igrave;nh ngủ cũng ch&iacute;nh l&agrave; l&uacute;c cơ thể cần được nu&ocirc;i dưỡng, th&agrave;nh phần casein (một loại protein)&nbsp; trong Muscle Mass Gainer gi&uacute;p hấp thu chậm trong 6 &ndash; 8 tiếng, l&uacute;c n&agrave;y cơ thể của bạn được nu&ocirc;i dưỡng ngay cả khi đang ngủ.</li>\r\n</ul>\r\n\r\n<p>Khi cơ thể đ&atilde; quen dần với sản phẩm v&agrave; lượng calories, t&ugrave;y theo nhu cầu tăng c&acirc;n nhanh, bạn c&oacute; thể d&ugrave;ng tới 9 muỗng / ng&agrave;y chia nhỏ l&agrave;m nhiều bữa phụ kh&aacute;c nhau.</p>\r\n', '159815686110.jpg', 1, 1700000, 12),
(13, 2, 'Premium Mass Gainer', '<p><strong>Premium Mass Gainer</strong>&nbsp;l&agrave;&nbsp;sữa tăng c&acirc;n&nbsp;cải tiến mới nhất từ MuscleTech - nh&atilde;n hiệu h&agrave;ng đầu.</p>\r\n', '<h3>1. GIỚI THI&Ecirc;̣U V&Ecirc;̀ PREMIUM MASS GAINER&nbsp;</h3>\r\n\r\n<p>Premium Mass Gainer&nbsp;được biết đến với thương hiệu&nbsp;MUSCLETECH&nbsp;h&agrave;ng đầu tại Mỹ về lĩnh vực thực phẩm bổ sung cao cấp d&agrave;nh cho vận đ&ocirc;ng vi&ecirc;n thể h&igrave;nh v&agrave; thể thao. Người ta thường nhắc đến Premium Mass Gainer theo đ&uacute;ng nghĩa của n&oacute; l&agrave; d&ograve;ng tăng c&acirc;n thượng hạng. Bạn tập Gym chuy&ecirc;n nghiệp hay theo phong tr&agrave;o th&igrave;&nbsp;Premium Mass Gainer&nbsp;vẫn l&agrave; sản phẩm kh&ocirc;ng thể thiếu trong những giai đoạn nhất định của bạn.</p>\r\n\r\n<p>Premium Mass Gainer&nbsp;có được những y&ecirc;́u t&ocirc;́ đ&ecirc;̉ trở thành dòng Sữa Tăng C&acirc;n được ưu chu&ocirc;̣ng nh&acirc;́t bởi thành ph&acirc;̀n, dưỡng ch&acirc;́t và hàm lượng Calo trong m&ocirc;̃i Serving của sản ph&acirc;̉m đủ đi&ecirc;̀u ki&ecirc;̣n cho vi&ecirc;̣c tăng c&acirc;n m&ocirc;̣t cách t&ocirc;́i ưu nh&acirc;́t. Là m&ocirc;̣t sản ph&acirc;̉m hoàn toàn mang đúng nghĩa&nbsp;thực ph&acirc;̉m b&ocirc;̉ sung&nbsp;h&ocirc;̃ trợ tăng c&acirc;n t&ocirc;́i ưu. Chứ kh&ocirc;ng phải g&oacute;i gọn trong một vi&ecirc;n thuốc nhỏ x&iacute;u để rồi bạn uống v&agrave;o, n&oacute; sẽ t&iacute;ch nước gi&uacute;p cho bạn mập l&ecirc;n sau đ&oacute; n&oacute; sẽ tụt&nbsp;xuống đ&uacute;ng theo những g&igrave; m&agrave; n&oacute; c&oacute;. Đ&oacute; l&agrave; mối nguy hại thật sự !!!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>2. NHỮNG ƯU ĐI&Ecirc;̉M CỦA PREMIUM MASS GAINER MANG TỚI CHO BẠN&nbsp;</h3>\r\n\r\n<p>Sản ph&acirc;̉m là một bữa ăn th&ocirc;ng minh được chiết xuất từ nguồn dinh dưỡng tự nhi&ecirc;n,&nbsp;cung cấp cho bạn đầy đủ dưỡng chất đa lượng v&agrave; vi lượng cần thiết nhất.</p>\r\n\r\n<p>▪️&nbsp;Premium Mass Gainer&nbsp;cung cấp cho bạn tới 1560calo, 67G Protein cao cấp v&agrave; 300G Energy-Providing Carbs tinh khiết khi chỉ pha th&ecirc;m với 500ml sữa tươi nguy&ecirc;n ch&acirc;́t,&nbsp;đ&acirc;́y chính là nguồn năng lượng kh&ocirc;ng thể thiếu cho mọi hoạt động h&agrave;ng ng&agrave;y của bạn, gi&uacute;p cho bạn ph&aacute;t triển cơ bắp, tăng c&acirc;n một c&aacute;ch hiệu quả nhất m&agrave; kh&ocirc;ng bị mỡ.</p>\r\n\r\n<p>▪️&nbsp;Premium Mass Gainer&nbsp;được chọn lựa v&agrave; sử dụng như một bữa ăn th&ocirc;ng minh thay thế cho bữa ăn của bạn, với hơn 20 loại vitamin v&agrave; kho&aacute;ng chất gi&agrave;u dinh dưỡng</p>\r\n\r\n<p>▪️&nbsp;Premium Mass Gainer&nbsp;c&ograve;n mang tới cho bạn 3.230mg Amino Acid quan trọng gi&uacute;p cho bạn tăng khả năng hấp thụ v&agrave; chuyển h&oacute;a dinh dưỡng 1 c&aacute;ch tối ưu. gi&uacute;p bạn phục hồi cơ bắp sau khi tập luyện.</p>\r\n\r\n<p>▪️&nbsp;Premium Mass Gainer&nbsp;c&oacute; hương vị tự nhi&ecirc;n mang lại cảm gi&aacute;c dịu nhẹ khi thưởng thức ( Đoạt giải thưởng hương vị của năm tổ chức tại Mỹ )</p>\r\n\r\n<p>▪️ Đăc biệt quan trọng l&agrave;&nbsp;Premium Mass Gainer&nbsp;được thiết kế bởi&nbsp;<u>MUSCLETECH</u>&nbsp;thương hi&ecirc;u h&agrave;ng đầu tại Mỹ về sản phẩm bổ sung cao cấp. Đoạt nhiều giải thưởng, v&agrave; đ&atilde; được những VDV v&ocirc; địch thế giới chuy&ecirc;n nghiệp như Philhealth, Jay Cutler,... tin tưởng sử dụng v&igrave; n&oacute; mang lại hiệu quả thật sự !</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>3. Đ&Ocirc;́I TƯỢNG N&Ecirc;N SỬ DỤNG PREMIUM MASS GAINER&nbsp;</h3>\r\n\r\n<h3>+ Người gầy muốn tăng c&acirc;n</h3>\r\n\r\n<p>+ Người tập thể h&igrave;nh v&agrave; thể thao</p>\r\n\r\n<p>+ Vận động vi&ecirc;n trong giai đoạn xả cơ</p>\r\n\r\n<p>+&nbsp;Người muốn tăng cơ giảm mỡ</p>\r\n\r\n<p>+ Người cần bổ sung cho chế độ ăn h&agrave;ng ng&agrave;y</p>\r\n\r\n<p>+ Người hấp thụ k&eacute;m, kh&oacute; tăng c&acirc;n</p>\r\n\r\n<h2>4. CÁCH SỬ DỤNG PREMIUM MASS GAINER M&Ocirc;̣T CÁCH HI&Ecirc;̣U QUẢ NH&Acirc;́T</h2>\r\n\r\n<p>Bạn d&ugrave;ng b&igrave;nh lắc sữa pha 2 muỗng&nbsp;Premium Mass Gainer&nbsp;( 1 lần d&ugrave;ng) t&ugrave;y theo chỉ số của bạn với 500ml nước lọc hoặc sữa tươi cho đến khi bột tan hết, hoặc cho v&agrave;o m&aacute;y xay sinh tố, c&oacute; thể kết hợp với c&aacute;c loại tr&aacute;i c&acirc;y, hoa củ quả t&ugrave;y theo sở th&iacute;ch của bạn.Nếu bạn muốn uống lạnh th&igrave; cho th&ecirc;m đ&aacute;, hương vị thật tuyệt vời. Bạn c&oacute; thể d&ugrave;ng v&agrave;o bất cứ khi n&agrave;o bạn cảm thấy đ&oacute;i, thay thế bữa ch&iacute;nh, &nbsp;d&ugrave;ng v&agrave;o c&aacute;c bữa phụ, trước khi tập, hoặc tối trước khi đi ngủ</p>\r\n\r\n<p>C&Aacute;CH D&Ugrave;NG PREMIUM MASS GAINER HIỆU QUẢ NHẤT:</p>\r\n\r\n<p><br />\r\nĐối với người mới bắt đầu sử dụng,&nbsp;Tuần đầu ti&ecirc;n n&ecirc;n c&oacute; giai đoạn l&agrave;m quen với sản phẩm:&nbsp;</p>\r\n\r\n<p>D&ugrave;ng 1 muỗng chia l&agrave;m 2 hoặc 3 lần sử dụng trong ng&agrave;y, mỗi lần&nbsp;1/3 muỗng (theo vạch chia) với 200 ml nước đun s&ocirc;i để nguội, hoặc nước lạnh, sữa tươi hoặc nước hoa quả.</p>\r\n\r\n<ul>\r\n	<li>Lần 1: 1/3 muỗng sau ăn s&aacute;ng 1 giờ.</li>\r\n	<li>Lần 2:&nbsp;1/3 muỗng v&agrave;o thời điểm giữa buổi chiều, sau ăn trưa tối thiểu 1 tiếng v&agrave; trước tập 1 tiếng.</li>\r\n	<li>Lần 3:&nbsp;1/3 muỗng ngay sau tập.</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; Từ tuần thứ 2, bắt đầu tăng l&ecirc;n với 2 muỗng/ng&agrave;y.&nbsp;Chia l&agrave;m 2 hoặc 3 lần sử dụng trong ng&agrave;y, mỗi lần&nbsp;2/3 muỗng (theo vạch chia) với 400 ml nước đun s&ocirc;i để nguội, hoặc nước lạnh, sữa tươi hoặc nước hoa quả.</p>\r\n\r\n<ul>\r\n	<li>Lần 1: 2/3 muỗng sau ăn s&aacute;ng 1 giờ.</li>\r\n	<li>Lần 2:&nbsp;2/3 muỗng v&agrave;o thời điểm giữa buổi chiều, sau ăn trưa tối thiểu 1 tiếng v&agrave; trước tập 1 tiếng.</li>\r\n	<li>Lần 3:&nbsp;2/3 muỗng ngay sau tập.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', '159815687011.jpg', 1, 1600000, 19),
(14, 3, 'Xtend Bcaa', '<p>Nếu bạn luyện tập đủ mạnh v&agrave; đủ để th&uacute;c đẩy cơ bắp bạn ph&aacute;t triển, cho d&ugrave; đ&oacute; l&agrave; ngay sau buổi tập luyện cường độ cao hay qu&atilde;ng thời gian d&agrave;i sau nghỉ ngơi, chắc chắn bạn sẽ thấy m&igrave;nh đang tr&ecirc;n bờ vực kiệt sức, mệt mỏi. Bạn muốn t&igrave;m cảm gi&aacute;c vượt qua giới hạn của bản th&acirc;n, đổ được nhiều mồ h&ocirc;i hơn, tập luyện mạnh mẽ hơn, v&agrave; chỉ cần th&ecirc;m 1 Rep.</p>\r\n', '<h3><br />\r\n1. GIỚI THI&Ecirc;̣U V&Ecirc;̀ XTEND BCAA - GIA TĂNG HI&Ecirc;̣U SU&Acirc;́T T&Acirc;̣P LUY&Ecirc;̣N</h3>\r\n\r\n<p><br />\r\nPhục h&ocirc;̀i nhanh hơn là chìa khóa đ&ecirc;̉ có k&ecirc;́t quả t&ocirc;́t hơn. Trong&nbsp;hơn 10 năm Scivation&nbsp;đã tạo ra những sản ph&acirc;̉m phục h&ocirc;̀i t&ocirc;́t nh&acirc;́t tr&ecirc;n th&ecirc;́ giới. Được cung c&acirc;́p t&acirc;̣n&nbsp;7G Axit Amin&nbsp;chu&ocirc;̃i nhánh BCAA đã được chứng minh l&acirc;m sàng h&ocirc;̃ trợ phục h&ocirc;̀i và tăng trưởng cơ bắp. Xtend BCAA&nbsp;cũng chứa các ch&acirc;́t đi&ecirc;̣n giải và các thành ph&acirc;̀n hi&ecirc;̣u su&acirc;́t b&ocirc;̉ sung đ&ecirc;̉ giúp bạn ti&ecirc;́p nhi&ecirc;n li&ecirc;̣u, sửa chữa và phục h&ocirc;̀i cơ bắp. Đặc bi&ecirc;̣t hơn&nbsp;Xtend BCAA&nbsp;được m&ecirc;̣nh danh&nbsp;là dòng BCAA u&ocirc;́ng ngon nh&acirc;́t trong t&acirc;́t cả các dòng&nbsp;BCAA&nbsp;có tr&ecirc;n thị trường.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xtend BCAA&nbsp;l&agrave; hỗn hợp chứa BCAAs được d&ugrave;ng trong thời gian tập luyện, n&oacute; được tạo ra để gi&uacute;p bạn tối ưu h&oacute;a cường độ tập luy&ecirc;n, ngăn chặn sự ph&aacute; hủy cơ v&agrave; gi&uacute;p cơ ph&aacute;t triển một c&aacute;ch tốt nhất.&nbsp;Xtend BCAA sở hữu c&ocirc;ng nghệ đường ti&ecirc;n tiến nhất v&agrave;&nbsp;free-carbohydrate&nbsp;sử dụng trong &nbsp;buổi tập , c&ocirc;ng thức khoa học để tối đa h&oacute;a cường độ luyện tập v&agrave; th&uacute;c đẩy sự tổng hợp protein cơ bắp tối ưu v&agrave; phục hồi trong khi bạn tập luyện.&nbsp;Xtend BCAA&nbsp;được x&acirc;y dựng với một sự pha trộn ch&iacute;nh x&aacute;c của hoạt chất PentActive, tham gia v&agrave;o qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh những tế b&agrave;o quan trọng đồng thời tối ưu h&oacute;a hiệu suất v&agrave; phản ứng của cơ bắp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>2. THÀNH PH&Acirc;̀N CHÍNH CỦA XTEND BCAA</h3>\r\n\r\n<p><br />\r\n▪️ 7G BCAA với tỉ l&ecirc;̣ 2:1:1. Tỷ l&ecirc;̣ đã được nghi&ecirc;n cứu và ki&ecirc;̉m chứng&nbsp;</p>\r\n\r\n<p>▪️ Th&ecirc; hi&ecirc;̣n hi&ecirc;̣u quả trong các nghi&ecirc;n cứu v&ecirc;̀ sản ph&acirc;̉m BCAA&nbsp;</p>\r\n\r\n<p>▪️ Kh&ocirc;ng chứa Sugar &amp; Carbohydrate&nbsp;</p>\r\n\r\n<p>▪️ 3.5G Leucine cho cơ bắp t&ocirc;̉ng hợp Protein t&ocirc;́t nh&acirc;́t&nbsp;</p>\r\n\r\n<p>▪️ 2.5G L-Glutamine &amp; 1G Citrulline Malate&nbsp;</p>\r\n\r\n<p>▪️ Hydration - Bù đắp ch&acirc;́t đi&ecirc;̣n giải&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>3. C&Ocirc;NG DỤNG CHÍNH CỦA XTEND BCAA MANG LẠI CHO BẠN&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>▪️ H&ocirc;̃ trợ tăng trưởng cơ bắp và sức mạnh&nbsp;</p>\r\n\r\n<p>▪️ H&ocirc;̃ trợ phục h&ocirc;̀i nhanh hơn&nbsp;</p>\r\n\r\n<p>▪️ Hạn ch&ecirc;́ quá trình dị hóa cơ bắp&nbsp;</p>\r\n\r\n<p>▪️ Gia tăng sức b&ecirc;̀n t&acirc;̣p luy&ecirc;̣n</p>\r\n\r\n<h2>4. HƯỚNG D&Acirc;̃N SỬ DỤNG XTEND BCAA&nbsp;&nbsp;M&Ocirc;̣T CÁCH HI&Ecirc;̣U QUẢ NH&Acirc;́T</h2>\r\n\r\n<p><br />\r\nH&ograve;a tan&nbsp;một muỗng với 600- 800ml&nbsp;nước lọc,&nbsp;uống trong l&uacute;c tập.</p>\r\n\r\n<p>Lưu &yacute;:&nbsp;KH&Ocirc;NG&nbsp;pha c&ugrave;ng nước n&oacute;ng, ngon hơn khi uống lạnh. C&oacute; thể uống v&agrave;o c&aacute;c thời điểm kh&aacute;c nhau trong ng&agrave;y, n&ecirc;n uống trong khi tập để được hiệu quả cao nhất.</p>\r\n', '159815688312.jpg', 1, 900000, 5),
(15, 3, 'Best Bcaa', '<p>Đ&acirc;y là dòng BCAA đ&acirc;̀u ti&ecirc;n k&ecirc;́t hợp ngu&ocirc;̀n BCAA l&acirc;̃n cả ch&acirc;́t CLA. Vừa giúp gia tăng sức b&ecirc;̀n t&acirc;̣p luy&ecirc;̣n đ&ocirc;̀ng thời giúp bạn chuy&ecirc;̉n hóa mỡ thừa thành năng lượng t&ocirc;́t hơn</p>\r\n', '<h2>I. GIỚI THI&Ecirc;̣U VÀ ĐI&Ecirc;̉M N&Ocirc;̉I B&Acirc;̣T CỦA BPI BEST BCAA</h2>\r\n\r\n<h3><br />\r\n1. BPI BEST BCAA LÀ GÌ?</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>BPI Sports Best BCAA&nbsp;kh&ocirc;ng phải l&agrave; một sản phẩm BCAA&nbsp;sử dụng c&aacute;c c&ocirc;ng thức cũ v&agrave; đ&atilde; hết thời, lạc hậu. N&oacute; l&agrave; c&ocirc;ng thức đ&acirc;̀u ti&ecirc;n li&ecirc;n k&ecirc;́t 3 nhánh chu&ocirc;̃i Axit Amin đó là:&nbsp;Leucine, Isoleucine và Valine&nbsp;- sử dụng một quy tr&igrave;nh c&ocirc;ng nghệ ti&ecirc;n tiến duy nhất v&agrave;&nbsp;độc quyền để h&igrave;nh th&agrave;nh chuỗi&nbsp;oligiopeptide&nbsp;(một chuỗi ngoại quan của c&aacute;c axit amin, v&iacute; dụ một&nbsp;li&ecirc;n kết chuỗi&nbsp;peptide ).&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c chuỗi axit amin ph&acirc;n nh&aacute;nh&nbsp;oligiopeptide&nbsp;c&oacute; thể k&iacute;ch hoạt v&agrave; hoạt động tốt hơn khi đi v&agrave;o cơ bắp, cho ph&eacute;p chuyển ho&aacute; hiệu quả hơn, hiệu năng&nbsp;lớn hơn, tăng cường thời gian tồn tạu trong m&ocirc; v&agrave; tế b&agrave;o, v&agrave; l&agrave;&nbsp;một m&ocirc; h&igrave;nh&nbsp;BCAA&nbsp;được cải thiện.&nbsp;</p>\r\n\r\n<h3><br />\r\n2. ĐI&Ecirc;̉M N&Ocirc;̉I B&Acirc;̣T CỦA BPI BEST BCAA&nbsp;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c khoa học đ&atilde; nghi&ecirc;n cứu&nbsp;về chuỗi&nbsp;oligiopeptide&nbsp;trong nhiều thập kỷ.&nbsp;V&agrave; trong những năm gần đ&acirc;y c&aacute;c nh&agrave; nghi&ecirc;n cứu h&agrave;ng đầu ng&agrave;y c&agrave;ng đi đến sự đồng thuận rằng trong h&igrave;nh thức n&agrave;y, dinh dưỡng protein, t&iacute;nh&nbsp;khả dụng, t&iacute;nh ổn định, độ h&ograve;a tan, v&agrave; khả năng truyền tải,v&agrave; trong số rất nhiều c&aacute;c yếu tố quan trọng kh&aacute;c&nbsp;- tất cả được giải quyết dưới dạng chuỗi n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>BPI Best BCAA&nbsp;là m&ocirc;̣t sản ph&acirc;̉m hoàn toàn thay đ&ocirc;̉i&nbsp;của BPI Sports với nhi&ecirc;̀u cải ti&ecirc;́n hơn so với những phi&ecirc;n bản cũ. Đặc đi&ecirc;̉m lớn nh&acirc;́t có th&ecirc;̉ đ&ecirc;̉ ý tới đó chính là bao bì ở b&ecirc;n ngoài đã được thay đ&ocirc;̉i hoàn toàn. Đặc đi&ecirc;̉m thứ 2 chúng ta có th&ecirc;̉ k&ecirc;̉ đ&ecirc;́n đó là bắt đ&acirc;̀u từ phi&ecirc;n bản 2020 sẽ kh&ocirc;ng h&ecirc;̀ có ch&acirc;́t tạo màu t&ocirc;̉ng hợp, qua đó giúp gia tăng đ&ocirc;̣ an toàn và ch&acirc;́t lượng của sản ph&acirc;̉m hơn r&acirc;́t nhi&ecirc;̀u.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>II. LỢI ÍCH VÀ CÁCH SỬ DỤNG BPI BEST BCAA&nbsp;</h2>\r\n\r\n<h3><br />\r\n1. NHỮNG LỢI &Iacute;CH TIỀM NĂNG CỦA BPI BEST BCAA MANG LẠI CHO BẠN&nbsp;</h3>\r\n\r\n<p><br />\r\nNhư đ&atilde; n&ecirc;u tr&ecirc;n, nh&aacute;nh chuỗi axit amin trong thức oligiopeptide, c&ugrave;ng trong&nbsp;Oligiopeptide-enzyme&nbsp;sử dụng C&ocirc;ng nghệ sinh học đang hoạt động, l&agrave; c&aacute;ch tốt nhất để khắc phục những hạn chế chuyển ho&aacute; v&agrave; sử dụng c&aacute;c hoạt chất đi v&agrave; ra v&agrave;o tế b&agrave;o&nbsp;v&agrave; ch&uacute;ng ta dễ d&agrave;ng&nbsp;nhận ra đầy đủ c&aacute;c lợi &iacute;ch&nbsp;- bao gồm:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>▪️ HỖ TRỢ CƠ BẮP TỔNG HỢP PROTEIN&nbsp;</p>\r\n\r\n<p>▪️ GIẢM QU&Aacute; TR&Igrave;NH DỊ H&Oacute;A, PH&Acirc;N HỦY PROTEIN</p>\r\n\r\n<p>▪️ TH&Uacute;C ĐẨY INSULIN&nbsp;</p>\r\n\r\n<p>▪️ HỖ TRỢ SẢN XUẤT GLUCOSE NỘI SINH&nbsp;</p>\r\n\r\n<p>▪️ PHỤC HỒI SỨC BỀN, TĂNG KHẢ NĂNG CHỊU ĐỰNG</p>\r\n\r\n<p>▪️ C&Ocirc;NG THỨC KẾT HỢP BCAA V&Agrave; CLA MATRIX VỪA TĂNG QU&Aacute; TR&Igrave;NH TỔNG HỢP CƠ BẮP VỪA GIẢM LƯỢNG MỠ THỪA TRONG M&Aacute;U.</p>\r\n\r\n<p>▪️ GIẢM CHOLESTEROL C&Oacute; HẠI TRONG MỠ M&Aacute;U</p>\r\n\r\n<h3>2. Cách sử dụng BPI BEST BCAA hi&ecirc;̣u quả nh&acirc;́t có th&ecirc;̉&nbsp;</h3>\r\n\r\n<h3><br />\r\nThời gian t&ocirc;́t nh&acirc;́t dùng Best BCAA là dùng trong lúc t&acirc;̣p luy&ecirc;̣n với 1 - 2 mu&ocirc;̃ng pha với 400-500ml nước. Tùy vào cảm nh&acirc;̣n vị của bạn đ&ecirc;̉ sử dụng sản ph&acirc;̉m t&ocirc;́t hơn&nbsp;</h3>\r\n\r\n<p>Sản ph&acirc;̉m BEST BCAA là m&ocirc;̣t sản ph&acirc;̉m v&ocirc; cùng linh hoạt bạn có th&ecirc;̉ dùng b&acirc;́t kì lúc nào bạn mu&ocirc;́n.</p>\r\n', '159815688913.jpg', 1, 450000, 0),
(16, 3, 'Hydro Bcaa', '<p>HYDRO BCAA là thành quả của m&ocirc;̣t sản ph&acirc;̉m pha tr&ocirc;̣n 1 cách hoàn hảo nh&acirc;́t giữa&nbsp;EAA và BCAA. Giúp tăng&nbsp;hi&ecirc;̣u su&acirc;́t t&acirc;̣p luy&ecirc;̣n của bạn được cao hơn và đ&ocirc;̀ng thời EAA cũng&nbsp;đóng vai trò t&ocirc;̉ng hợp&nbsp;Protein t&ocirc;́t hơn&nbsp;đ&ecirc;̉ phát tri&ecirc;̉n cơ bắp&nbsp;</p>\r\n', '<h2>1. BCAA TRONG&nbsp;HYDROBCAA LÀ GÌ ?</h2>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<p>Mọi người đ&ecirc;̀u nhắc tới BCAA trong tập luyện hoặc là&nbsp;trong l&uacute;c tập bạn thấy c&oacute; người u&ocirc;́ng thứ nước màu vàng, xanh, đỏ. V&acirc;ng, c&acirc;u trả lời ch&iacute;nh l&agrave; BCAA</p>\r\n\r\n<p>BCAA l&agrave; 3 nh&aacute;nh acid amin thu&ocirc;̣c&nbsp;tổng số 21 loại acid amin được tạo l&ecirc;n từ&nbsp;ph&acirc;n tử Protein ho&agrave;n chỉnh. BCAA bao gồm:</p>\r\n\r\n<p>◾️ Leucine : là m&ocirc;̣t acid amin v&ocirc; cùng thi&ecirc;́t y&ecirc;́u nhằm ph&acirc;n tách các ch&acirc;́t béo trong cơ th&ecirc;̉. Đ&ocirc;̀ng thời giúp t&ocirc;̉ng hợp Protein trong cơ bắp và giúp kích hoạt cơ ch&ecirc;́ x&acirc;y dựng cơ bắp ở trong cơ th&ecirc;̉&nbsp;</p>\r\n\r\n<p>◾️ Valine:&nbsp;được chia cắt qua tinh bột, gi&uacute;p ngăn ngừa cơ bắp dị ho&aacute;, nghĩa l&agrave; qu&aacute; tr&igrave;nh l&agrave;m mất cơ bắp, biến cơ bắp th&agrave;nh chất đơn giản để cơ thể c&oacute; thể sử dụng.&nbsp;</p>\r\n\r\n<p>◾️ ISO Leucine: được chia cắt qua cả 2. Gi&uacute;p x&acirc;y dựng v&agrave; k&iacute;ch th&iacute;ch cơ bắp ph&aacute;t triển, giảm t&iacute;ch luỹ mỡ thừa v&agrave;o cơ thể.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>3 th&agrave;nh phần n&agrave;y tạo n&ecirc;n BCAAs, trong qu&aacute; tr&igrave;nh vận động, cơ bắp sử dụng BCAAs, oxy ho&aacute; BCAAs tạo th&agrave;nh năng lượng ATP gi&uacute;p bạn vận động v&agrave; đẩy tạ.&nbsp;Việc ph&aacute;t triển cơ bắp cần BCAAs đến thến nhưng rất tiếc cơ thể bạn kh&ocirc;ng thể tự tổng hợp được BCAAs m&agrave; phải bổ sung từ ngo&agrave;i v&agrave;o như thực phẩm hay thực phẩm bổ sung.&nbsp;</p>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>2. T&Aacute;C DỤNG CH&Iacute;NH CỦA&nbsp;HYDROBCAA</h3>\r\n\r\n<p><br />\r\n◾️ Giảm đau nhức cơ bắp sau khi t&acirc;̣p&nbsp;<br />\r\n◾️ Tránh tình trạng dị hóa cơ bắp và ngăn chặn vi&ecirc;̣c m&acirc;́t cơ bắp&nbsp;<br />\r\n◾️ Gia tăng sức chịu đựng và cải thi&ecirc;̣n sức b&ecirc;̀n t&acirc;̣p luy&ecirc;̣n.&nbsp;<br />\r\n◾️ Gia tăng&nbsp;insulin v&agrave; hooc m&ocirc;n tăng trưởng trong bu&ocirc;̉i t&acirc;̣p<br />\r\n◾️ Gia tăng cường độ tập luyện v&agrave; mức độ testosterone◾️ K&iacute;ch th&iacute;ch sự tổng hợp protein<br />\r\n◾️ Đẩy nhanh qu&aacute; tr&igrave;nh tổng hợp Protein, chống lại qu&aacute; tr&igrave;nh dị ho&aacute;, tăng đồng ho&aacute;, chống lại đau nhức mệt mỏi trong tập luyện. Tăng tốc h&igrave;nh th&agrave;nh cơ</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>3. HƯỚNG DẪN SỬ DỤNG&nbsp;HYDROBCAA</h3>\r\n\r\n<p>Sử dụng vào t&acirc;́t cả các thời đi&ecirc;̉m ở trong ngày đ&ecirc;̀u được nhưng thời đi&ecirc;̉m sử dụng t&ocirc;́t nh&acirc;́t là ở trong bu&ocirc;̉i t&acirc;̣p&nbsp;</p>\r\n\r\n<p>Sử dụng trong lúc&nbsp;tập 1 muỗng với 400-450&nbsp;ml nước, uống&nbsp;trong tập&nbsp;để tăng khả năng&nbsp;ph&aacute;t triển cơ v&agrave; khả năng phục hồi</p>\r\n\r\n<p>&nbsp;</p>\r\n', '159815689614.jpg', 0, 600000, 0),
(17, 5, 'Lipo 6 Black', '<p>LIPO-6 BLACK ULTRA CONCENTRATE&nbsp;cho đến nay l&agrave; một trong những chất đốt ch&aacute;y chất b&eacute;o mạnh nhất. N&oacute; mạnh đến nỗi bạn kh&ocirc;ng bao giờ c&oacute; thể uống nhiều hơn một vi&ecirc;n mỗi khẩu phần.</p>\r\n', '<p><span style=\"font-size:16px\">1. Giới thi&ecirc;̣u v&ecirc;̀&nbsp;LIPO-6 BLACK ULTRA CONCENTRATE</span><br />\r\n&nbsp;</p>\r\n\r\n<p>▪️ Để gi&uacute;p đảm bảo rằng mục ti&ecirc;u ăn ki&ecirc;ng v&agrave; giảm c&acirc;n của bạn trở n&ecirc;n th&agrave;nh c&ocirc;ng,&nbsp;LIPO-6 BLACK ULTRA CONCENTRATE&nbsp;chứa thử nghiệm l&acirc;m s&agrave;ng đ&atilde; được thử nghiệm l&acirc;m s&agrave;ng gi&uacute;p tăng tốc độ trao đổi chất (sinh nhiệt) v&agrave; hỗ trợ kiểm so&aacute;t sự th&egrave;m ăn. Yohimbine v&agrave; Rauwolscine sẽ hỗ trợ th&ecirc;m cho hiệu ứng sinh nhiệt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>▪️ Để chắc chắn rằng bạn sẽ đạt được những kết quả to lớn nhất c&oacute; thể,&nbsp;LIPO-6 BLACK ULTRA CONCENTRATE<strong>&nbsp;</strong>gi&uacute;p bạn giảm c&aacute;m gi&aacute;c th&egrave;m ăn. Hỗ trợ qu&aacute; tr&igrave;nh sinh nhiệt v&agrave; gi&uacute;p bạn lu&ocirc;n cảm thấy tr&agrave;n đầy năng lượng, cảm gi&aacute;c n&agrave;y sẽ theo bạn nhiều giờ li&ecirc;n tục. Để đảm bảo hiệu quả tối ưu&nbsp;LIPO-6 BLACK ULTRA CONCENTRATE&nbsp;được n&eacute;n trong 1 vi&ecirc;n đ&oacute;ng g&oacute;i dạng con nhộng v&agrave; b&ecirc;n trong dạng lỏng - điều m&agrave; &iacute;t c&oacute; sản phẩm n&agrave;o thực hiện&nbsp;V&agrave; với sức mạnh như vậy bạn chỉ cần 1 vi&ecirc;n duy nhất v&agrave; ho&agrave;n th&agrave;nh mọi vấn đề đang vướng phải, thừa c&acirc;n, qu&aacute; nhiều mỡ thừa. H&Atilde;Y NHỚ RẰNG: Đ&acirc;y&nbsp;l&agrave; một sản phẩm&nbsp;PH&Aacute;&nbsp;HỦY CHẤT B&Eacute;O THỪA&nbsp;kh&ocirc;ng giống như những sản phẩm n&agrave;o từng c&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">2. Thành ph&acirc;̀n của&nbsp;LIPO-6 BLACK ULTRA CONCENTRATE&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Caffeine</p>\r\n\r\n<p>Caffeine l&agrave; một chất có trong tự nhi&ecirc;n và có&nbsp;khả năng k&iacute;ch th&iacute;ch cơ thể v&agrave; tr&iacute; n&atilde;o. Caffeine&nbsp;gi&uacute;p ch&uacute;ng ta đốt ch&aacute;y mỡ thừa hiệu quả.</p>\r\n\r\n<p>Sinh nhiệt v&agrave; đốt mỡ</p>\r\n\r\n<p>Sinh nhiệt chuyển đổi calo th&agrave;nh năng lượng nhiệt giúp đ&ocirc;́t cháy mỡ thừa. Lượng calo th&ecirc;m n&agrave;y hỗ trợ trong việc giảm c&acirc;n v&agrave; đốt ch&aacute;y chất b&eacute;o v&agrave; duy tr&igrave; 1 sức khỏe tốt. Tuy nhi&ecirc;n ch&uacute;ng ta chưa biết được cơ chế m&agrave; caffeine k&iacute;ch th&iacute;ch sự sinh nhiệt.</p>\r\n\r\n<p>Chromium</p>\r\n\r\n<p>Chromium c&oacute; thể gi&uacute;p ch&uacute;ng ta giữ mức đường trong m&aacute;u b&igrave;nh thường bằng c&aacute;ch cải thiện c&aacute;ch cơ thể ch&uacute;ng ta sử dụng insulin.</p>\r\n\r\n<p>▪️ Hạn chế th&egrave;m ăn</p>\r\n\r\n<p>▪️ Đốt nhiều calo hơn</p>\r\n\r\n<p>▪️ Cắt giảm mỡ thừa</p>\r\n\r\n<p>▪️ Tăng khối lượng cơ bắp</p>\r\n\r\n<p>N-Acetyl-L-Tyrosin</p>\r\n\r\n<p>C&aacute;c Axit Amin L-tyrosine l&agrave; một trong những th&agrave;nh phần gi&uacute;p bộ n&atilde;o tạo ra c&aacute;c dẫn truyền thần kinh như dopamine. L-tyrosine gi&uacute;p bạn giảm c&acirc;n bằng c&aacute;ch th&uacute;c đẩy sự trao đổi chất.</p>\r\n\r\n<p>C&aacute;c nghi&ecirc;n cứu đ&atilde; cho thấy n&oacute; c&oacute; thể gi&uacute;p ngăn chặn sự th&egrave;m ăn khi sử dụng kết hợp với c&aacute;c sản phẩm giảm c&acirc;n c&oacute; chứa caffeine, ephedrine hoặc phenylpropanolamine . Tuy nhi&ecirc;n, Cục Quản l&yacute; Thuốc v&agrave; Thực phẩm đ&atilde; cấm phenylpropanolamine do c&oacute; thể dẫn đến nguy cơ đột quỵ, L-tyrosine c&oacute; thể l&agrave;m tăng cảm gi&aacute;c hạnh ph&uacute;c của bạn khi chế lượng calo nạp v&agrave;o. (theo Claude Bouchard v&agrave; George A. Bray, c&aacute;c bi&ecirc;n tập vi&ecirc;n s&aacute;ch &quot;Sổ tay của B&eacute;o ph&igrave;: Ứng dụng l&acirc;m s&agrave;ng&quot;)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:16px\">3. Những lợi ích của&nbsp;LIPO-6 BLACK ULTRA CONCENTRATE&nbsp;</span></h2>\r\n\r\n<p>▪️ C&Ocirc;NG THỨC C&Ocirc; ĐẶC GI&Uacute;P MANG LẠI SI&Ecirc;U KẾT QUẢ CHO QU&Aacute; TR&Igrave;NH ĐỐT MỠ THỪA</p>\r\n\r\n<p>▪️ CHỈ CẦN MỘT VI&Ecirc;N DUY NHẤT</p>\r\n\r\n<p>▪️ K&Iacute;CH HOẠT QU&Aacute; TR&Igrave;NH TRAO ĐỔI CHẤT V&Agrave; ĐỐT NHIỀU CALORIES HƠN</p>\r\n\r\n<p>▪️ ĐỐT MỠ NHANH HƠN - TIẾP X&Uacute;C NHANH HƠN VỚI VI&Ecirc;N CON NHỘNG V&Agrave; B&Ecirc;N TRONG L&Agrave; CHẤT LỎNG</p>\r\n\r\n<p>▪️ SỬ DỤNG TRONG 1 TH&Aacute;NG ĐỂ NHẬN THẤY KẾT QUẢ</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>4. Hướng d&acirc;̃n sử dụng&nbsp;LIPO-6 BLACK ULTRA CONCENTRATE&nbsp;hi&ecirc;̣u quả nh&acirc;́t&nbsp;</h3>\r\n\r\n<p>Dùng 1 vi&ecirc;n vào bu&ocirc;̉i sáng khi ngủ d&acirc;̣y. Dùng 1 vi&ecirc;n vào bu&ocirc;̉i chi&ecirc;̀u trước t&acirc;̣p 30 phút. Sử dụng đ&ecirc;̀u đặn đúng chu kỳ đ&ecirc;̉ đạt hi&ecirc;̣u quả t&ocirc;́t nh&acirc;́t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khuy&ecirc;́n cáo<strong>&nbsp;</strong>:&nbsp;Đ&acirc;y l&agrave; sản phẩm đốt mỡ mạnh nhất m&agrave; ch&uacute;ng t&ocirc;i từng sản xuất. N&oacute; mạnh tới mức m&agrave; bạn chỉ cần sử dụng một vi&ecirc;n duy nhất. Đ&acirc;y l&agrave; c&ocirc;ng thức c&ocirc; đặc mở rộng mang lại si&ecirc;u sức mạnh chỉ trong một vi&ecirc;n duy nhất n&oacute; thiết kế để gi&uacute;p bạn nhanh ch&oacute;ng ph&aacute; hủy những nơi m&agrave; cơ thể bạn đang t&iacute;ch lũy mỡ cả cả loại mỡ lỳ lợm nhất.&nbsp;</p>\r\n', '159815690315.jpg', 0, 900000, 17),
(18, 5, 'Yến mạch Quaker', '<p>Yến mạch ( t&ecirc;n gọi tiếng anh l&agrave; Oat) l&agrave; một loại ngũ cốc được trồng chủ yếu ở c&aacute;c v&ugrave;ng c&oacute; khi hậu &ocirc;n đới như Mỹ, Canada, Ba Lan, Nga, Đức, &Uacute;c.</p>\r\n', '<p><strong>Bột yến mạch Quaker Oats</strong>&nbsp;l&agrave; sự lựa chọn l&yacute; tưởng cho những ai đang c&oacute; nhu cầu giảm c&acirc;n bởi th&agrave;nh phần của n&oacute;<strong>&nbsp;</strong>chứa một lượng lớn chất sơ h&ograve;a tan, tạo cảm gi&aacute;c no l&acirc;u v&agrave; th&uacute;c đẩy qu&aacute; tr&igrave;nh trao đổi chất gi&uacute;p cơ thể trở n&ecirc;n khỏe mạnh hơn.</p>\r\n\r\n<p>Ngo&agrave;i t&aacute;c dụng giảm c&acirc;n ra th&igrave;&nbsp;<strong>bột yến mạch</strong>&nbsp;c&ograve;n l&agrave;m giảm lượng cholesterol xấu c&oacute; trong m&aacute;u v&agrave; hỗ trợ tốt trong việc điều trị bệnh tiểu đường v&agrave; tim mạch.</p>\r\n\r\n<h2><strong>Th&agrave;nh phần c&oacute; trong bột yến mạch Quaker Oats</strong></h2>\r\n\r\n<p>Để c&oacute; thể vừa giảm c&acirc;n m&agrave; vẫn cung cấp đầy đủ dưỡng chất cho cơ thể hoạt động cả ng&agrave;y th&igrave; trong th&agrave;nh phần bột yến mạch cần chứa nhiều chất dinh dưỡng. Cụ thể trong bột yến mạch c&oacute; chứa c&aacute;c th&agrave;nh phần dinh dưỡng sau: 389 Kcal, 7g Lipit, 2mg Natri, 429 mg Kali, 66g Cabohydrat, 11g chất xơ, 17g Protein, 54mg Canxi, 4.7mg sắt, 177mg Magie v&agrave; c&ugrave;ng nhiều kho&aacute;ng chất kh&aacute;c</p>\r\n\r\n<h2><strong>Bột yến mạch Quaker Oats &ndash; thực phẩm giảm c&acirc;n an to&agrave;n v&agrave; hiểu quả</strong></h2>\r\n\r\n<p>Theo ph&acirc;n t&iacute;ch của c&aacute;c nh&agrave; chuy&ecirc;n gia đưa ra th&igrave; bột yến mạch khi uống sẽ tạo cảm gi&aacute;c no l&acirc;u, hạn chế cảm gi&aacute;c th&egrave;m ăn v&agrave; đốt ch&aacute;y lượng chất b&eacute;o trong cơ thể nhanh hơn. Đặc biệt, bột&nbsp;<strong>yến mạch Quaker Oats</strong>&nbsp;gi&uacute;p hấp thụ được lượng chất sơ gấp đ&ocirc;i so với c&aacute;c loại bột kh&aacute;c v&agrave; kh&ocirc;ng chứa chất b&eacute;o, đường, it Calor.</p>\r\n\r\n<h3><strong>Lưu &yacute; khi sử dụng bột yến mạch để giảm c&acirc;n hiệu quả nhất</strong></h3>\r\n\r\n<p>Để giảm c&acirc;n một c&aacute;ch hiệu quả nhất th&igrave; bạn n&ecirc;n kết hợp uống bột yến mạch v&agrave; thực hiện th&ecirc;m c&aacute;c việc sau:</p>\r\n\r\n<ul>\r\n	<li>V&agrave;o mỗi buổi s&aacute;ng bạn cần uống 250ml &ndash; 350ml nước ấm để l&agrave;m sạch hệ ti&ecirc;u h&oacute;a v&agrave; gi&uacute;p dạ d&agrave;y th&iacute;ch nghi nhanh ch&oacute;ng.</li>\r\n	<li>Uống đủ 2 l&iacute;t nước mỗi ng&agrave;y.</li>\r\n	<li>Kết hợp với tập thể dục 15-20 ph&uacute;t mỗi ng&agrave;y sẽ giảm c&acirc;n&nbsp;hiệu quả.</li>\r\n	<li>Ăn th&ecirc;m c&aacute;c bữa phụ bằng trứng luộc, sữa chua v&agrave; c&aacute;c loại hoa quả it đường để bổ sung vitamin.</li>\r\n</ul>\r\n\r\n<h3><strong>C&aacute;c bảo quản bột yến mạch Quaker Oats 4kg</strong></h3>\r\n\r\n<p>Bột yến mạch l&agrave; một loại ngũ cốc kh&aacute; l&agrave;nh t&iacute;nh n&ecirc;n rất dễ mốc. Ch&iacute;nh v&igrave; vậy bạn n&ecirc;n đựng trong hộp k&iacute;n, để nơi tho&aacute;ng m&aacute;t v&agrave; tr&aacute;nh &aacute;nh nắng trực tiếp từ mặt trời. Nếu bạn c&oacute; tủ lạnh th&igrave; đ&oacute; l&agrave; nơi tốt nhất để bảo quản bột&nbsp;<strong>yến mạch Quaker Oats</strong>&nbsp;n&agrave;y.</p>\r\n\r\n<p>Bột&nbsp;<strong>yến mạch Quaker Oats 4kg</strong>&nbsp;l&agrave; một sự lựa chọn tuyệt vời d&agrave;nh cho bạn. Bạn sẽ c&oacute; một th&acirc;n h&igrave;nh đpẹ, một v&oacute;c d&aacute;ng như mong muốn khi kết hợp giảm c&acirc;n bằng bột yến mạch với chế độ ăn uống hợp l&yacute;.</p>\r\n', '159815691116.jpg', 0, 450000, 0);

